--

CREATE OR REPLACE VIEW public."Kierán's query for mistyped page numbers" AS
 SELECT l.lemma_id,
    l.original,
    l.translation
   FROM (public.lemmata l
     JOIN public.quotations USING (lemma_id))
  WHERE ((l.language_id = 6) AND ((quotations.page)::text !~~ '%.%'::text))
  GROUP BY l.lemma_id
  ORDER BY l.lemma_id;


