INSERT INTO public.quotations VALUES (744, 'Ἀμφόριστοι ἀστέρες εἰσὶν ὅσοι τῷ μὲν Ἡλίῳ εἰσὶν ἑῷοι, τῷ δὲ ὡροσκόπῳ ἑσπέριοι.', '', 637, 'Planets “bounded on both sides” are those to the east of the Sun and to the west of the Ascendant.', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '', 'CCAG VIII.4', 'https://archive.org/details/cataloguscodipart408bruxuoft/page/230/mode/2up', '', 859, '230', '');
INSERT INTO public.quotations VALUES (443, 'GE₆ 22 ina ZALAG₂ sin ar₂ MUL₂ ar₂ ša₂ ALLA ša₂ ULU₃ 2 1/2 KUŠ₃ sin 1 KUŠ₃ ana ULU₃ SIG', '', 410, 'Night of (day) 22, last part of the night, Sin was 2 1/2 cubits behind the rear star of the crab to the south, Sin being 1 cubit low to the south.', 'BM 32839+ (ADRT III No. -141F)', 'astronomical diary', 'Babylon', '142 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 6''', 552, '124-125', '');
INSERT INTO public.quotations VALUES (1008, 'Zodiacus orbis, in quo duodecim signa infixa sunt', '', 901, 'The circle of the zodiac in which the twelve signs are fixed', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.1', 1106, '1.41', '');
INSERT INTO public.quotations VALUES (804, 'U₄.1.KAM ana DU₃-ka', '', 719, 'In order for you to compute day 1.', 'BM 36722 (BMAPT No. 52)', 'mathematical astronomy: procedure text', 'Babylon', 'Seleucid', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'obv. i 39', 914, '', '');
INSERT INTO public.quotations VALUES (676, 'ἡ προγεναμένη ἀπόκρο̣υ̣σις ἐ̣γ̣έ̣νετο Διδύμοις μυρ(ῶν) βʹ λε(πτῶν) μθʹ', '', 606, 'The preceding “breaking” (i.e., point opposite the prenatal syzygy?) was in Gemini 2 degrees 49 minutes', 'PSI I 23a', 'horoscope', 'Antinoopolis', '338 CE', '(GH 338)', '', '17–18', 799, '', '');
INSERT INTO public.quotations VALUES (394, 'GI₆ 23 ina ZALAG₂ GU₄.UD SIG SI MAŠ₂ 2 KUŠ₃ 8 SI', '', 368, 'Night of (day) 23, last part of the night, Mercury was 2 cubits 8 fingers below the horn of the goat-fish.', 'BM 46229 (ADRT I No. -346)', 'astronomical diary', 'Babylon', '347 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 28', 502, '146-147', '');
INSERT INTO public.quotations VALUES (328, 'GI₆ 11 sin TA DIRI ki E.A SIG DILI ša₂ IGI ABSIN 1 KUŠ₃ sin 1/2 KUŠ₃ ana NIM DIB', '', 314, 'Night of (day) 11, when Sin emerged from a cloud, it was 1 cubit below the single (star) in front of the Furrow, passing 1/2 cubit to the east.', 'BM 45708 (ADRT III No. -125A)', 'astronomical diary', 'Babylon', '126 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 4', 422, '258-259', '');
INSERT INTO public.quotations VALUES (351, '[i]ti.KIN 3 mul2.ELLAG2 1.40 mul2.MAŠ.TAB.BA.GAL.GAL 50 mul2.MAR.GID2.DA', '', 331, '[M]onth VI: 3 ''The Kidney'', 1,40 ''The Great Twins'', 50 ''The Wagon''.', 'BM 34713 (LBAT 1499)', 'schematic astronomy: ''three stars each'' + rising times', 'Babylon', '4th-2nd cent BCE', 'Horowitz, W., 2014. The Three Stars Each: The Astrolabes and Related Texts (Archiv für Orientforschung, Beiheft 33). Horn/Wien: Berger & Söhne/Institut für Orientalistik der Universität Wien.', '', 'obv.6', 446, '126', '');
INSERT INTO public.quotations VALUES (464, 'u₃ APIN 30 TA 14 ša₂ DU₆ a-di 12 ša₂ APIN', '', 429, 'and month VIII (begins on day) 30 (of the preceding month): from (day) 14 of month VII until (day) 12 of month VIII', 'BM 36722', 'mathematical astronomy: procedure text', 'Babylon', 'Seleucid', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'ii 3', 579, '348-349', '');
INSERT INTO public.quotations VALUES (619, 'DIŠ mul.AŠ.IKU šu-bat d.E2-a a-lik IGI MUL.MEŠ šu-ut d.A-nim', '', 555, '¶ The Field, the seat of Ea, who goes in front of the stars of Anu.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'i 40', 733, '38', '');
INSERT INTO public.quotations VALUES (835, 'KI ni-ṣir-tu4 ša2 d.UTU mul.lu2.ḪUN.GA2 d.Du[mu-zi . . .]', '', 752, 'The place of secret of the Sun is the Hired Man constellation (Aries), Du[muzi …].', 'BM 35407+', 'calendar treatise', 'Babylon', '4th-2nd century BCE', 'Reynolds, F., 2019. A Babylon Calendar Treatise: Scholars and Invaders in the Late First Millennium. Oxford: Oxford University Press.', '', 'i 30''', 942, '194', '');
