INSERT INTO public.quotations VALUES (2605, 'Σελήνη ἐν Ταύρῳ ζῳδίῳ θηλυκῷ οἴκῳ [Ἀφροδίτης]', '', 2272, 'The Moon (was) in Taurus, a female zodiac sign, house of Venus', 'P.Oxy. 235', 'horoscope', '', '', '(GH 15/22)', '', '9', 2667, '', '');
INSERT INTO public.quotations VALUES (196, 'Σελήνης ἑσπερία ἀνατολὴ λέγεται ὅταν ἀπὸ συνόδου φέρηται μέχρι πανσελήνου, ἑῴα δὲ ἀνατολὴ ἀπὸ πανσελήνου μέχρι συνόδου', '', 198, 'It is called the Moon’s western rising whenever it moves from conjunction (with the Sun) to full Moon, and (the Moon’s) eastern rising (whenever it moves) from full Moon to conjunction', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 291, '227', '');
