INSERT INTO public.quotations VALUES (775, '', 'ỉn ỉw=f ẖn pr ỉw=f glg gꜣ pr ỉw=f ḫf', 692, 'If it (moon) is in a place which is established or a place which is destroyed', 'P.Carlsberg XI 10', 'literary text', 'Tebtunis', '2nd Cent. CE', 'Quack, J. F. and K. Ryholt, 2019, Petese Interpreting Astrology by Imhotep for King Nechepsos. Copenhagen, 2019, 161-183.', '', '14', 885, '165', '');
INSERT INTO public.quotations VALUES (447, 'GI₆ 12 ⸢SI GIN⸣ [ina] ⸢ZALAG₂⸣ MUL₂.BABBAR SIG GIŠ.KUN A 4 1/2 KUŠ₃', '', 413, 'Night of (day) 12, the north wind blew; last part of the night, Jupiter was 4 1/2 cubits below the rump of the lion.', 'BM 34609 (ADRT III No. -144)', 'astronomical diary', 'Babylon', '145 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 4', 556, '96-97', '');
INSERT INTO public.quotations VALUES (742, 'i-nu-šu₂ MUL₂.BABBAR ina MAŠ₂ dil-bat ina GIR₂.TAB', '', 665, 'At that time, Jupiter was in Capricorn, Venus in Scorpio', 'BM 33667', 'horoscope', 'Babylon', 'Seleucid era', 'Rochberg, F. 1998. Babylonian Horoscopes. Transactions of the American Philosophical Society, 88, 1–164.', '', 'u.e. 1', 857, '73-74', '');
INSERT INTO public.quotations VALUES (329, 'GE₆ 5 ina ZALAG₂ MUL₂.BABBAR ar₂ SA₄ ša₂ [ABSIN]', '', 315, 'Night of (day) 5, last part of the night, Jupiter was behind the bright one of the [Furrow].', 'BM 40099 (ADRT I No. -332B)', 'astronomical diary', 'Babylon', '333 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 7''', 423, '172-173', '');
INSERT INTO public.quotations VALUES (306, 'Συνέβαινε γὰρ περὶ τούτους τοὺς τόπους τὴν μὲν νύκτα παντελῶς μικρὰν γίνεσθαι ὡρῶν οἷς μὲν δύο, οἷς δὲ τριῶν, ὥστε μετὰ τὴν δύσιν μικροῦ διαλείμματος γινομένου ἐπανατέλλειν εὐθέως τὸν ἥλιον.', '', 297, 'For it happens around these places that the night becomes utterly short, two hours for some, three for others, so that while a small pause is taking place after the setting, the sun is rising straightaway again.', 'Geminus', 'astronomy', '', '1st century BCE', 'Geminus. 1898. Γεμίνου Εἰσαγωγή εἰς τὰ Φαινόμενα, Gemini Elementa Astronomiae, ed. C.H.A. Manitius. Leipzig: B.G. Teubner.', 'http://www.wilbourhall.org/pdfs/GeminusElementaAstronomiae.pdf', '6.9', 405, '70, 72', '');
