INSERT INTO public.quotations VALUES (555, 'DIŠ MUL ne2-bu-u2 ša2 mul.UZ3 d.LAMMA SUKAL d.Ba-U2', '', 499, '¶ The bright star of the Goat: Lammu, the vizier of Baba.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'i 26', 670, '35', '');
INSERT INTO public.quotations VALUES (465, 'TA 27 zib EN 13 ABSIN₀ AB₂ ana AB₂ 1.58.45.42 TAB u LA₂ lib₃ -bu-u₂ ša₂ MURUB₄ -tu₂ KUR-ad₂', '', 429, 'From 27 Psc until 13 Vir you add and subtract 1,58;45,42 month by month, whereby you reach the central part.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'Rev. i 21', 579, '360', '');
INSERT INTO public.quotations VALUES (2612, 'וְזָרַ֥ח הַשֶּׁ֖מֶשׁ וּבָ֣א הַשָּׁ֑מֶשׁ וְאֶ֨ל־מְקוֹמ֔וֹ שׁוֹאֵ֛ף זוֹרֵ֥חַֽ ה֖וּא שָֽׁם׃', 'Wəzāraḥ haššemeš ûb̲āʾ haššāmeš wəʾel-məqômô šôʾēp̲ zôrēaḥ hûʾ šām.', 2271, 'Also, the sun hath risen, and the sun hath gone in, and unto its place panting it is rising there.', 'Ecclesiastes', 'religious text', '', '', '', '', '1,5', 2672, '', '');
INSERT INTO public.quotations VALUES (146, 'βοηθοὶ δὲ λέγονται ἀστέρες οἱ συλλαμβανόμενοι ἢ ἐπιμαρτυροῦντες ἢ μεσεμβολοῦντες', '', 156, 'Helpers are called the stars that jointly possess (a zodiac sign) or are in aspect or are intervening', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 5, '226', '');
INSERT INTO public.quotations VALUES (389, '9 GU₄.UD ina NIM ina MAŠ.MAŠ-IGI KUR NIM-a 18.30 na-su in 5 IGI', '', 365, '(Day) 9, Mercury’s morning rising in Gemini; it was bright (and) high, its presence (NA) was 18;30; (ideal) appearance on (day) 5.', 'BM 45608 (ADRT II -209D)', 'astronomical diary', 'Babylon', '210 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 5', 499, '180-181', '');
INSERT INTO public.quotations VALUES (695, 'Φαέθων Διός', '', 622, 'The bright one (Jupiter), (the star) of Zeus', 'IGLS I 36', 'horoscope', '', '62 BCE?', '(GH –61)', '', '', 817, '', '');
INSERT INTO public.quotations VALUES (924, 'DIŠ 30 ina IGI.LA2-šu2 TA U4-1-KAM EN U4-5-KAM | 5 u4-mi U4.SAKAR d.A-ni7 | TA U4-6-KAM EN U4-10-KAM | 5 u4-mi ka-li-tu2 d.E2-a | TA U4-11-KAM EN U4-15-KAM 5 u4-mi AGA taš-ri-iḫ-ti | [ip-pir]-ma d.En-lil2', '', 331, 'The moon at its appearance from the frist day tot he fifth day, | five days, is a crescent, it is Anu; | from the sixth day tot he tenth day, | five days, is a kidney, it is Ea; | from the eleventh day tot he fifteenth day, five days, the crown of splendour | it wears, it is Enlil.', 'K 250+ (CT 26 40-41)', 'star list', 'Nineveh', '7th cent BCE', 'Koch-Westenholz, U., 1995. Mesopotamian Astrology. An Introduction to Babylonian and Assyrian Celestial Divination. Copenhagen: Museum Tusculanum Press.', '', 'vi 16-21', 43, '202', '');
INSERT INTO public.quotations VALUES (1063, 'cum Solis radiis fuerit oppressus, naturalem enim malitiam Solem veneratus amittit', '', 953, 'for in being subservient to the Sun [Mars] loses his natural malefic qualities', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.8.2', 1157, '1.51', '');
INSERT INTO public.quotations VALUES (981, 'sed ad liquidum propter partium minutorumque brevitatem ac velocissimum siderum cursum caelique pronum rotatae vertiginis lapsum neminem pervenire posse confirmant', '', 876, 'But they add that no one can get definite results because of the smallness of the degrees and minutes, the tremendous speed of the stars'' orbits, and the inclination of the heavens.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.3.2', 1074, '1.9', '');
INSERT INTO public.quotations VALUES (419, 'οἱ τριγωνικοὶ δεσπόται τοῦ ὡροσκόπου καὶ τοῦ αἱρετικοῦ φωτὸς ἀπέκλιναν ἐναντιούμενοι ἑαυτοῖς καὶ ὑπὸ Ἄρεως θεωρούμενοι', '', 389, 'The masters of the triangles of the Ascendant and of the luminary of the sect were in cadent places, being in opposition to themselves and under the aspect of Mars', 'CCAG VIII.4', 'horoscope', '', '463', '(GH L 463)', '', 'pp. 224–225', 528, '', '');
INSERT INTO public.quotations VALUES (448, '', '[ⲁϥⳓⲱ ⲉⲟ]ⲩⲛⲧϥ ⲡⲣⲉ ⲻⲥⲟⲩϧⲁⲛ ⲥⲟⲩⲕⲏ ⲻⲧⲟⲩⲁⲣⳗⲁⳗⲉ ⲻⲡⲓⲥ[ⲟⲩ ...]', 391, '[if] he [had] the sun as a friendly star and Mercury, they were hostile to this st[ar ...', 'P.Lond. 98', 'horoscope', 'Egypt', '95', 'J. Černý, P.E. Kahle, and R.A. Parker, 1957, The Old Coptic Horoscope, Journal of Egyptian Archaeology 43, 86-100.', '', '118', 557, '87; 97', '');
INSERT INTO public.quotations VALUES (1262, 'Διαιρεῖται δὲ ὁ ἐνιαύσιος χρόνος εἰς μέρη δ᾽, ἔαρ θέρος φθινόπωρον χειμῶνα.', '', 1123, 'The time of a year is divided into 4 parts: spring, summer, autumn, winter.', 'Geminus', 'astronomy', '', '1st century BCE', 'Geminus. 1898. Γεμίνου Εἰσαγωγή εἰς τὰ Φαινόμενα, Gemini Elementa Astronomiae, ed. C.H.A. Manitius. Leipzig: B.G. Teubner.', 'http://www.wilbourhall.org/pdfs/GeminusElementaAstronomiae.pdf', '1.9', 1354, '6', '');
INSERT INTO public.quotations VALUES (321, 'GI₆ 15 ina [ZALAG₂] ⸢sin⸣ ina IGI MAŠ.MAŠ IGI 2 KUŠ₃', '', 309, 'Night of (day) 15, last [part of the night], Sin was 2 cubits in front of the front twin (star).', 'BM 34362+ (ADRT II -197B)', 'astronomical diary', 'Babylon', '198 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 15', 417, '250-251', '');
INSERT INTO public.quotations VALUES (307, 'dil-bat ina RIN₂ ina MURUB₄ ITI ina GIR₂', '', 298, 'Venus was in Libra, in the middle of the month in Scorpio.', 'BM 33837 (ADRT II No. -232)', 'astronomical diary', 'Babylon', '233 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 12', 406, '104-105', '');
INSERT INTO public.quotations VALUES (355, 'ἀρχέτυπος λέγεται ὁ καλούμενος κλῆρος τύχης, τὰ δὲ τούτου τετράγωνα ὑπόστασις', '', 335, 'The “exemplar” is called the so-called “lot of fortune,” and its quadrangles are the “substance”', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '', 'CCAG VIII.4', '', '', 451, '227', '');
INSERT INTO public.quotations VALUES (620, '(8'') [TA 13 R]IN₂ EN 27 zib.ME ITI ana I[TI]
(9'') [2.6.15.4]2 TAB u LA₂-ka', '', 556, '(8’) [From 13 Li]b until 27 Psc, month by m[onth] (9’) your addition and subtraction is [2,6;15,4]2.', 'BM 34148 (BMAPT No. 58)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'rev. 8''-9''', 735, '388-389', '');
INSERT INTO public.quotations VALUES (466, '⸢GI₆⸣ 26 ŠU₂.ŠU₂ ina ZALAG₂ sin ina IGI MUL₂ e ša₂ SAG GIR₂ 1 1/2 KUŠ₃', '', 430, 'Night of (day) 26, very overcast; last part of the night, Sin was 1 1/2 cubits in front of the upper star of the head of the scorpion.', 'BM 33837 (ADRT II No. -232)', 'astronomical diary', 'Babylon', '233 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 21', 582, '106-107', '');
INSERT INTO public.quotations VALUES (330, '(37) 4.30 IB₂.TAG₄ (38) U₄.28.KAM 4.30 ⸢ME⸣ NIM.A ana KUR E.A GAR-an', '', 316, '(37) 4,30 is the remainder (38) of day 28. You put down 4,30 as the time (from New Moon) to sunrise in order to predict KUR.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'rev. ii 37-38', 424, '368-369', '');
INSERT INTO public.quotations VALUES (1076, 'Afelioti, quem nos Solanum dicimus, Gemini, Libra, Aquarius', '', 966, 'To the afeliotes, which we call the east wind, are subject the signs Gemini, Libra, Aquarius', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.12', 1171, '1.55', '');
INSERT INTO public.quotations VALUES (189, '¶ iti.KIN mul.PAN d.INANNA ELAM.MA.ki.KE₄ <DUMU.MUNUS AN.NA.KE₄>', '', 192, 'Month VI, constellation of the Bow, "Elamite" Inanna,', 'KAV 218', 'astrolabe', 'Nineveh', 'Neo-Assyrian', 'Casaburi, M. C. 2003. Tre-stelle-per-ciascun (-mese): l''Astrolabio B: edizione filologica. Annali/Supplemento', '', 'a ii 16', 279, '37', '');
INSERT INTO public.quotations VALUES (669, 'DIŠ MUL ša2 EGIR-šu2 GUB-zu mul.DAR.LUGAL', '', 601, '¶ The star which stands behind it: the Rooster.', 'Rm 2, 174+313', 'schematic astronomy: mulapin', 'Nineveh', 'ca. 911-612 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'i 13''', 790, '39-40', '');
INSERT INTO public.quotations VALUES (579, '', 'r dỉ.t ḫpr=f ꜥn n ỉr pꜣ nty pḥ n wš nb r-ẖ(.t) tꜣ ẖ.t nty-ỉw tꜣ p.t smn ḥr-ꜣt.ṱ={w}⟨=s⟩ n pꜣ hrw', 524, '... in order to allow it to also happen as is fitting at all times according  to the manner which the sky is established today', 'Canopus Decree, Tanis', 'inscription', 'Tanis', '3rd century BCE', 'S. Pfeiffer, 2008, Das Dekret von Kanopos (238 v. Chr.): Kommentar und historische Auswertung eines dreisprachigen Synodaldekretes der ägyptischen Priester zu Ehren Ptolemaios'' III. und seiner Familie.', '', '39', 693, '', '');
INSERT INTO public.quotations VALUES (154, '', 'n-ṯꜣy pꜣ gn⟨ḥ⟩ḏ 6 šꜥ ỉḫ.t 26 r swsw 110', 162, 'From Cancer 6° to Libra 26° is 110 degrees', 'ANAshm.Mus.D.O. 483', 'mathematical astronomy: procedure text', 'Egypt', '3rd cent. BCE to 3rd cent. CE', 'Ossendrijver, M. and A. Winkler, 2018. Chaldeans on the Nile: Two Egyptian Procedure Texts with Babylonian System A1 and A2 for Mercury, in Scaffolding of Our Thoughts: Essays on Assyriology and the History of Science in Honor of Fracesca Rochberg, eds. J. Crisostomo et al. (Leiden: Brill, 2018), 282-418', '', '5', 227, '386 - 387', '');
INSERT INTO public.quotations VALUES (444, '⸢ina ZALAG₂ dil-bat SIG⸣ SAG A 4 KUŠ₃', '', 411, 'Last part of the night, Venus was 4 cubits below the head of the lion.', 'BM 33850 (ADRT III No. -162)', 'astronomical diary', 'Babylon', '163 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 7', 553, '24-25', '');
INSERT INTO public.quotations VALUES (356, 'ἀρχέτυπος λέγεται ὁ καλούμενος κλῆρος τύχης, τὰ δὲ τούτου τετράγωνα ὑπόστασις', '', 336, 'The “exemplar” is called the so-called “lot of fortune,” and its quadrangles are the “substance”', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 453, '227', '');
INSERT INTO public.quotations VALUES (331, '(11) ki-i sin [ma]-la a-ma-ri la ma-ṣu-u₂ (12) ZI ᵈsin ša₂ 1-en u₄-mu KI-šu₂ TAB-ma ana kun-nu E-bi', '', 316, '(11) If Sin is insufficient(ly bright) for being visible (12) you add Sin’s displacement for 1 day to it and you predict a full month.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'rev. ii 11-12', 424, '364-365', '');
INSERT INTO public.quotations VALUES (372, 'GI6 19 19 DIRI AN DIB ULU₃ ŠAR₂', '', 352, 'Night of (day) 19, (daytime of day) 19: clouds crossed the sky, gusty wind.', 'BM 34651 (ADRT I No. -342A)', 'astronomical diary', 'Babylon', '343 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 11', 478, '', '');
INSERT INTO public.quotations VALUES (2613, 'וְזָרַ֥ח הַשֶּׁ֖מֶשׁ וּבָ֣א הַשָּׁ֑מֶשׁ וְאֶ֨ל־מְקוֹמ֔וֹ שׁוֹאֵ֛ף זוֹרֵ֥חַֽ ה֖וּא שָֽׁם׃', 'Wəzāraḥ haššemeš ûb̲āʾ haššāmeš wəʾel-məqômô šôʾēp̲ zôrēaḥ hûʾ šām.', 2276, 'Also, the sun hath risen, and the sun hath gone in, and unto its place panting it is rising there.', 'Ecclesiastes', 'religious text', '', '', '', '', '1 5', 2673, '', '');
INSERT INTO public.quotations VALUES (1522, 'Si Luna et Sol synodica fuerint radiatione coniuncti, et in eodem signo inter Solem et Lunam Mars medius fuerit inventus, et eos de quadrato Saturnus respiciat [...], qui sic habuerint stellas, vitiosis oppressi humoribus interibunt', '', 1350, 'If Moon and Sun are in conjunction (by sign) and Mars is located in the same sign between them while Saturn stands in square aspect to them—he who has the planets configured as such will die oppressed by malignant humors', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.29.15', 1614, '2.136-137', '');
INSERT INTO public.quotations VALUES (1092, 'filiorum in quinto', '', 980, 'the [place] of the children [is] in the fifth [sign]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.14.3', 1188, '1.58', '');
INSERT INTO public.quotations VALUES (467, 'GE₆ 18 ina ZALAG₂ sin ina IGI SI₄ 1 KUŠ₃', '', 431, 'Night of the 18th, last part of the night, Sin was 1 cubit in front of Lisi.', 'BM 45816 (ADRT I No. -380B)', 'astronomical diary', 'Babylon', '381 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 8''', 583, '86-87', '');
INSERT INTO public.quotations VALUES (556, 'mul.Lam-mu SUKKAL d.Ba-U2', '', 499, 'Lammu, vizier of Baba.', 'BM 42262 (5R, 46, 1)', 'star list', 'Borsippa', 'Achaemenid or Seleucid period.', 'Wee, J. Z., 2016. A Late Babylonian Astral Commentary on Marduk''s Address to the Demons, Journal of Near Eastern Studies 75.', '', '17', 670, '162', '');
INSERT INTO public.quotations VALUES (1088, 'spei vel pecuniae in secundo horoscopi signo', '', 977, 'of Expectation of Inheritance and Wealth in the second sign from the ascendant', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.14.3', 1185, '1.58', '');
INSERT INTO public.quotations VALUES (32, 'DIŠ 30 ina mul.Zi-ba-ni-tum AN.GE6 GAR-ma (…)', '', 76, 'If the moon is eclipsed in Libra (…)', 'BM 36746+', 'zodiacal astrology: celestial divination', 'Babylon', 'ca. 400-300 BCE', 'Rochberg, Francesca. 1984. New evidence for the history of astrology. JNES 43: 115-140.', '', 'obv. 16''', 86, '', '');
INSERT INTO public.quotations VALUES (3, 'DIS mul.GU4.AN.NA mul.AB.SIN2 u mul.[SUḪUR.MAŠ] 3 KI.MEŠ a-n[a kur].⸢NIM?⸣.[MA.ki]', '', 4, 'The Bull of Heaven (Taurus), the Furrow (Virgo), and the Goat-Fish (Capricorn): 3 areas for [Elam].', 'BM 47494', 'astrological compendium', 'Babylon?', '464-337 BCE', 'Hunger, H. 2004. Stars, Cities, and Predictions. Pp. 16–32 in Studies in the History of the Exact Sciences in Honour of David Pingree, eds. Charles Burnett, Jan Hogendijk, Kim Plofker and Michio Yano. Leiden: Brill.', '', 'rev. 17-18', 9, '', '');
INSERT INTO public.quotations VALUES (539, 'GE₆ 12 MURUB₄ sin TUR₃ NIGIN₂ AN ⸢ina⸣ ŠA₃ GUB', '', 272, 'Night of (day) 12, middle part of the night, Sin was surrounded by a halo, Mars stood in it.', 'BM 35195 (ADRT I No. -378)', 'astronomical diary', 'Babylon', '379 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 5''', 371, '90-91', '');
INSERT INTO public.quotations VALUES (103, '', '[pꜣy-ms=w] r pꜣ-nṯr-twꜣ mtw⸗f rꜥ-ḫꜥ ỉw=f r ỉr rmṯ ḥsy', 126, '[The one who was born] when Venus was in the ascendant, he will become a praised man', 'P.Berlin 8345', 'astrological treatise', 'Soknopaiou Nesos', '1st cent. CE to 2nd cent. CE', 'Quack, J. F., 2008, Texte aus Ägypten: Demotische, magische und divinatorische Texte. In B. Janowski and B. Wilhelm (eds), Omina, Orakel, Rituale und Beschwörungen. Texte aus der Umwelt des Alten Testaments, Neue Folge 4. Gütersloh: Gütersloher Verlagshaus.', '', 'x+1, 2', 172, '', '');
INSERT INTO public.quotations VALUES (326, '12 d.SAG.ME.GAR ana ME E₁₁', '', 311, '(Day) 12, Jupiter rose to daylight.', 'VAT 4956 (ADRT I No. -567)', 'astronomical diary', '?', '568 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 4', 418, '46-47', '');
INSERT INTO public.quotations VALUES (806, '(33) 4 a-na 30 ŠEŠ.MEŠ SE₃-ma IGI 30 : 2
(34) 4 A.RA₂ 2 8 ⸢8⸣ A.RA₂ 4 tam-ṭi-tu₄ DU-ma
(35) 32', '', 721, '(33) You divide 4 into 30 parts: the reciprocal of 30 is (0);2, (34) 4 times 2 is (0);8. You multiply (0);8 by 4, the deficit, it is (35) (0);32.', 'BM 36722 (BMAPT No. 52)', 'mathematical astronomy: procedure text', 'Babylon', 'Seleucid', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'obv. i 33-35', 916, '', '');
INSERT INTO public.quotations VALUES (523, 'Ἥλιος Ζυγῷ εʹ ηʹ, Σελήνη Ταύρῳ ηʹ, δʹ, Κρόνος Ταύρῳ κεʹ, Ζεὺς Ζυγῷ κγʹ ηʹ, Ἄρης Αἰγοκέρῳ κϛʹ ηʹ, Ἀφροδίτη Σκορπίῳ κϛʹ, Ἑρμῆς Ζυγῷ κγʹ, ὡροσκόπος Ὑδροχόῳ κγʹ λʹ, μεσουράνημα Ταύρῳ εʹ γʹ, πανσέληνος Κριῷ βʹ μβʹ, Τύχη Παρθένῳ κϛʹ λδʹ, Δαίμων Καρκίνῳ κʹ λδʹ, ὕψωμα γενέσεως Παρθένῳ, κλῆρος ἀναιρέτου Ὑδροχόῳ.', '', 478, 'Sun in Libra 5;08, Moon in Taurus 8;04, Saturn in Taurus 25, Jupiter in Libra 23;08, Mars in Capricorn 26;08, Venus in Scorpio 26, Mercury in Libra 23, Ascendant in Aquarius 23;30, Midheaven in Taurus 5;03, (prenatal) full Moon in Aries 2;42, Fortune in Virgo 26;34, Daimōn in Cancer 20;34, exaltation of the nativity in Virgo, lot of the destroyer in Aquarius.', 'Rhetorius, fragments', 'horoscope', '', '6th–7th cent. CE', 'CCAG VIII.4', '', '§113', 641, '221 (GH L 440)', '');
INSERT INTO public.quotations VALUES (309, '', 'ḥsb.t 4.t ỉbd-2 ꜣḫ.t sw 9 ṯꜣy-wnw.t 11 rhwy', 299, 'Regnal year 4, month 2 of Akhet, day 9, 11th hour of the night.', 'O.Neugeb. 2', 'horoscope', 'Thebes', '1st cent.CE', 'O. Neugebauer, “Demotic Horoscopes”, JAOS 63 (1943), 115–127.', '', '1', 407, '116-117', '');
INSERT INTO public.quotations VALUES (357, '“Strong” planets are called Saturn, Jupiter, Mars. A “strong” zodiac sign is called the tenth and ninth in very nativity (counting) from the Moon, that is, the right quadrangle and triangle', 'κραταιοὶ ἀστέρες λέγονται Κρόνος, Ζεύς, Ἄρης. ζῴδιον κραταιὸν καλεῖται ἑκάστης γενέσεως τὸ δέκατον καὶ ἔννατον ἀπὸ Σελήνης· τουτέστι τὸ δεξιὸν τετράγωνον καὶ τρίγωνον', 337, '', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 455, '227', '');
INSERT INTO public.quotations VALUES (1009, 'Signa autem ipsa duodecim his nominibus nominantur: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpius, Sagittarius, Capricornus, Aquarius et Pisces.', '', 902, 'The twelve signs themselves are called by these names: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpio, Sagittarius, Capricorn, Aquarius, and Pisces.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.2', 1107, '1.41', '');
INSERT INTO public.quotations VALUES (398, '[ⲁϥϬⲱ ⲉⲟ]ⲩⲛⲧϥ ⲡⲣⲉ ⲺⲥⲟⲩϦⲁⲛ ⲥⲟⲩⲕⲏ ⲛⲧⲟⲩⲁⲣⳖⲁⳖⲉ', '[afcô eo]untf pre–souxan soukê ntouarčače', 372, 'If he has the sun as a friendly star with Mercury, and they were at the enmity', 'P.Lond. 98', 'horoscope', 'Egypt', '95', 'J. Černý, P.E. Kahle, and R.A. Parker, 1957, The Old Coptic Horoscope, Journal of Egyptian Archaeology 43, 86-100.', '', '118', 506, '97', '');
INSERT INTO public.quotations VALUES (1087, 'spei vel pecuniae in secundo horoscopi signo', '', 976, 'of Expectation of Inheritace and Wealth in the second sign from the ascendant', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.14.3', 1184, '1.58', '');
INSERT INTO public.quotations VALUES (515, 'DIŠ Iš₈-tar2 ina iti.še-er-BURU14 ina KUR 20 KUR-ma ⸢TUR3⸣ ⸢NIGIN2⸣ KUR ELAM.MA.ki me-sir IGI', '', 471, 'If Venus in the month of the Harvest Furrow rises at sunrise and is surrounded by a
INSERT INTO public.quotations VALUES (390, 'GI₆ 8 SAG GI₆ sin ar₂ MUL₂ KUR ša₂ DUR nu-nu 1/2 KUŠ₃', '', 241, 'Night of (day) 8, beginning of the night, Sin was 1/2 cubit behind the bright star of the ribbon of the fishes.', 'BM 46229 (ADRT I No. -346)', 'astronomical diary', 'Babylon', '347 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 4', 328, '142-143', '');
INSERT INTO public.quotations VALUES (2305, 'medio cum luna implebitur orbe', '', 1350, 'When in mid-course the Moon is full', 'Manilius, Astronomica', 'astrological treatise', '', '', 'Manilius. Astronomica. Edited and translated by G. P. Goold. Loeb Classical Library 469. Cambridge, MA: Harvard University Press, 1977.', 'https://www.loebclassics.com/view/manilius-astronomica/1977/pb_LCL469.41.xml', '1.469', 1614, '40-41', '');
INSERT INTO public.quotations VALUES (1948, '⸢22⸣ ina KIN.SIG bu-tuq TIR.AN id SI u KUR GAL₂-ši', '', 1733, 'The 22nd, in the afternoon, there was a section of a rainbow in the direction of north and east.', 'BM 45927 (ADRT I No. -284)', 'astronomical diary', 'Babylon', '-284', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', 'http://oracc.museum.upenn.edu/adsd/adart1/corpus', 'obv. 10''', 2037, '294', '');
INSERT INTO public.quotations VALUES (373, 'GI6 19 19 DIRI AN DIB ULU₃ ŠAR₂', '', 353, 'Night of (day) 19, (daytime of day) 19: clouds crossed the sky, gusty wind.', 'BM 34651 (ADRT I No. -342A)', 'astronomical diary', 'Babylon', '343 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 11', 480, '', '');
INSERT INTO public.quotations VALUES (212, '', 'ỉw=k wꜣḥ nkt r pꜣy=f ỉry n-ỉm=w r mꜣꜥ ḫꜥ 1513 ꜥn', 222, 'You add the numbers to one each other it makes the part of rising 1513 again', 'ANAshm.O.D. 641.9-10', 'horoscope', 'Athribis', '1st cent. BCE - 1st cent. CE', 'Winkler, A., 2022. The First zodiac Sign and the Daimon: The Advent of an Astrological Tradition and Seven Elaborate Horoscopes, Studien zur Altägyptischen Kultur 51:', '', '11', 307, '386', '');
INSERT INTO public.quotations VALUES (516, '(10'') NIM u SIG ša₂ ḪAB AN.MI (11'') A.RA₂ 20 DU ina NIM (12'') KI 17.24 TAB ina SIG (13'') ZI', '', 472, '(10'') The height and depth of the disk of the eclipse (11'') you multiply by (0);20. When it is high (12'') you add it with 17;24; when it is low (13'') you tear it out.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. ib 10-13', 634, '360-361', '');
INSERT INTO public.quotations VALUES (1220, 'nam in curvis signis praemori facit, 5 interduoi claudos et gibberosos.', '', 1094, 'In crooked signs he makes for an early death and sometimes produces cripples or hunchbacks.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.4.16', 1312, '1.119', '');
INSERT INTO public.quotations VALUES (451, '', 'ḥsb.t 23.t n ỉꜥḥ rnp.t ꜥꜣ.t tꜣy', 219, 'Numbering year 23 of the Moon is a great year.', 'P.Carlsberg 9', 'astronomy', 'Soknopaiou Nesos', 'Second cent. CE', 'Neugebauer, O. & Parker, R.A. EAT 3', '', 'iii 17', 560, '225', '');
INSERT INTO public.quotations VALUES (807, '(33) 4 a-na 30 ŠEŠ.MEŠ SE₃-ma IGI 30 : 2
(34) 4 A.RA₂ 2 8 ⸢8⸣ A.RA₂ 4 tam-ṭi-tu₄ DU-ma
(35) 32', '', 722, '(33) You divide 4 into 30 parts: the reciprocal of 30 is (0);2, (34) 4 times 2 is (0);8. You multiply (0);8 by 4, the deficit, it is (35) (0);32.', 'BM 36722 (BMAPT No. 52)', 'mathematical astronomy: procedure text', 'Babylon', 'Seleucid', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'obv. i 33-35', 917, '344-357', '');
INSERT INTO public.quotations VALUES (421, '[i]ti.ZIZ2 2.40 mul2.NU.MUŠ.DA 1.10 mul2.⌈ALLA⌉ 35 mul2.TI8.m[uše]n', '', 392, 'Month XI: 2,40 Numušda, 1,10 ''The Crab'', 35 ''The Eagle''.', 'BM 34713 (LBAT 1499)', 'schematic astronomy: ''three stars each'' + rising times', 'Babylon', '4th-2nd cent BCE', 'Horowitz, W., 2014. The Three Stars Each: The Astrolabes and Related Texts (Archiv für Orientforschung, Beiheft 33). Horn/Wien: Berger & Söhne/Institut für Orientalistik der Universität Wien.', '', 'obv. 11', 530, '126', '');
INSERT INTO public.quotations VALUES (1194, 'ὡσαύτως δὲ καὶ ἐνθάδε ἐν δισώμοις οἱ ἀστέρες ἔτυχον', '', 1076, 'And so too here did the planets happen to be in two-bodied (zodiac signs).', 'Vettius Valens', 'astrological treatise', '', '2nd century CE', '(GH L 134,VI)', '', '7.6.44', 1288, '', '');
INSERT INTO public.quotations VALUES (492, '(10'') [GE₆ 23] (11'') ina ZALAG₂ sin ina IGI MUL₂ IGI ša₂ ALLA ša₂ ⸢ULU₃⸣ 1 KUŠ₃', '', 453, '(10'') [Night of (day) 23], last part of the night, Sin was 1 cubit in front of the front star of the crab to the south.', 'BM 32843 (ADRT III No. -133A)', 'astronomical diary', 'Babylon', '134 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 10''-11''', 603, '198-199', '');
INSERT INTO public.quotations VALUES (374, 'GE₆ 5 SAG GE₆ ŠU₂.ŠU₂ IM ŠAR₂ GIR₂ GU₃ U AN kab-bar PISAN DIB', '', 354, 'Night of (day) 5, beginning of the night, very overcast, gusty wind, lightning, thunder, thick rain, PISAN DIB.', 'BM 32889+ (ADRT II No. -246)', 'astronomical diary', 'Babylon', '247 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 2', 481, '58-59', '');
INSERT INTO public.quotations VALUES (557, 'DIŠ 2 MUL.MEŠ ša2 EGIR-šu2 GUB.MEŠ-zu d.Nin-SAR u d.Er3-ra-gal', '', 500, '¶ The two stars which stand behind it: Nin-nisig and Erragal.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'i 27', 671, '35', '');
INSERT INTO public.quotations VALUES (21, '', 'ꜣbd-3 šmw tꜣ rpy', 65, 'Epiphi: Virgo', 'O.Strasb.Dem. 521.9', 'astronomical list', '', '300 BCE - 300 CE', 'Neugebauer, O., 1943. Demotic Horoscopes, Journal of the American Oriental Society 63: 121-122', '', '', 65, '', '');
INSERT INTO public.quotations VALUES (580, '[ki-ma] MUL.MEŠ šu-ut 50 ug-da-me-ru-ni', '', 507, 'When the stars of Enlil have been finished, (...).', 'ND 5497/22', 'schematic astronomy: mulapin', 'Kalhu', 'ca. 800-600 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'i 16''', 676, '37', '');
INSERT INTO public.quotations VALUES (402, 'iti.GAN 2 mul2.Ṣal-bat-a-nu 1.10 mul2.GIR2.TAB 35 mul2.[LU]GAL', '', 81, 'Month IX: 2 Mars, 1,10 ''The Scorpion'', 35 ''The [Ki]ng''.', 'BM 34713 (LBAT 1499)', 'schematic astronomy: ''three stars each'' + rising times', 'Babylon', '4th-2nd cent BCE', 'Horowitz, W., 2014. The Three Stars Each: The Astrolabes and Related Texts (Archiv für Orientforschung, Beiheft 33). Horn/Wien: Berger & Söhne/Institut für Orientalistik der Universität Wien.', '', 'obv. 9', 93, '126', '');
INSERT INTO public.quotations VALUES (648, '23 MUL.MEŠ šu-ut d.A-nim', '', 582, '23 stars of Anu.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'ii 18', 762, '42', '');
INSERT INTO public.quotations VALUES (670, 'DIŠ MUL d.Za-ba4-ba4 mul.TI8.mušen u mul.AD6', '', 602, '¶ The Star of Zababa, the Eagle, and the Corpse.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'ii 12', 791, '41', '');
INSERT INTO public.quotations VALUES (2008, 'Ob hoc omnia eiusmodi simulacra candida sunt et similia lunaribus circulis, quia ex percussu oblique accepto sole respondent.', '', 1787, 'For this reason all phenomena of this type are white and similar to moon-shaped discs, because they reflect bright light when they are struck obliquely by the sun.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.73.xml', '1.13.2', 2096, '72-73', '');
INSERT INTO public.quotations VALUES (269, 'mul2.GU.LA a-dir 30 ina EN.NUN.U4.ZAL.LA AN.GE6 GAR-ma', '', 273, 'The Great One is dark: the Moon makes an eclipse in the morning watch.', 'AO 6455 (TCL VI 11 = TU 11)', 'collection of astrological and astronomical rules', 'Uruk', 'ca. 200 BCE', 'Brack-Bernsen, Lis, and Hunger, Hermann. 2002. TU 11. A Collection of Rules for the Prediction of Lunar Phases and of Month Lengths. SCIAMVS 3: 3–90.', '', 'obv. 2', 372, '7', '');
INSERT INTO public.quotations VALUES (1041, 'quem nos Iovem vocamus, Aegypti Faethontem vocant', '', 932, 'the one we call Jupiter the Egyptians call Faetho', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.2.2', 1136, '1.42', '');
INSERT INTO public.quotations VALUES (268, 'mul2.SIM.MAḪ a-dir 30 ina EN.NUN.MURUB4.BA AN.GE6 GAR-ma', '', 272, 'The Swallow is dark: the Moon makes an eclipse in the middle watch.', 'AO 6455 (TCL VI 11 = TU 11)', 'collection of astrological and astronomical rules', 'Uruk', 'ca. 200 BCE', 'Brack-Bernsen, Lis, and Hunger, Hermann. 2002. TU 11. A Collection of Rules for the Prediction of Lunar Phases and of Month Lengths. SCIAMVS 3: 3–90.', '', 'obv. 1', 371, '7', '');
INSERT INTO public.quotations VALUES (187, 'ἐν ἰδίαις λαμπήναις λέγονται οἱ ἀστέρες ὅταν ἐπὶ τῶν ἰδίων ὑψωμάτων τύχωσι', '', 191, 'The planets are said to be “in their own covered chariots” whenever they are at their own exaltations', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 278, '227', '');
INSERT INTO public.quotations VALUES (359, 'τὸ πρῶτον τρίγωνον καὶ τετράγωνον καὶ ἑξάγωνον λέγεται τὰ εὐώνυμα †ἑξῆς ἡ Σελήνη πρῶτον συνάπτει τινὶ τῶν ἀστέρων', '', 339, 'The “first triangle” and “(first) quadrangle” and “(first) hexagon” are called the left(-aspect) ones, starting from when(?) the Moon first makes contact with any of the planets', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 459, '227', '');
INSERT INTO public.quotations VALUES (70, 'lu2.TUR a-lid-ma d.SAG.ME.GAR E3-ma d.GENNA ŠU2 LU2 BI SIG5-iq EN DU11-šu2 UG7', '', 97, 'If a child is born when Jupiter comes forth and Saturn sets, it will go excellently with that man; his personal enemy will die.', 'AO 6483 (TCL 6 14 = TU 14)', 'horoscopic astrology', 'Uruk', '250-150 BCE', 'Sachs, A., 1952. Babylonian Horoscopes, Journal of Cuneiform Studies 6: 49-75.', '', 'rev. 9', 128, '', '');
INSERT INTO public.quotations VALUES (62, 'ina ni-ip-ṣu ša2 ᵈSAG.ME.GAR ina mul2.UR.A AN ina mul2.GU A.AN MAḪ', '', 75, 'At the "smashing" (opposition?) of Jupiter in the Lion, Mars in the Great One: heavy rain.', 'AO 6449 (TCL VI 19 = TU 19)', 'astrological weather prediction', 'Uruk', '13 August 226 BCE (Seleucid Era year 84, 12 V)', 'Hunger, H. 1976. Astrologische Wettervorhersagen. Zeitschrift fur Assyriologie 66.', '', 'rev. 1', 85, '248', '');
INSERT INTO public.quotations VALUES (1075, 'Afelioti, quem nos Solanum dicimus, Gemini, Libra, Aquarius', '', 965, 'To the afeliotes, which we call the east wind, are subject the signs Gemini, Libra, Aquarius', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.12', 1170, '1.55', '');
INSERT INTO public.quotations VALUES (766, 'Κρόνος Κριοῦ κδʹ κηʹ (...) ἀπὸ πρώτου τοῦ στηριγμοῦ, ἀφαιρέτης, ἑσπέριος φαινόμενος', '', 683, 'Saturn, in Aries 24;28 (...) appearing in the evening after the first station, in retrograde', 'CCAG IV p. 107', 'horoscope', '', '', '', '', '', 876, '', '');
INSERT INTO public.quotations VALUES (422, '[i]ti.ŠE 3 mul2.KU6 1.20 mul2.SIM.MAḪ 40 mul2.Da-mu', '', 393, 'Month XII: 3 ''The Fish'', 1,20 ''The Swallow'', 40 Damu.', 'BM 34713 (LBAT 1499)', 'schematic astronomy: ''three stars each'' + rising times', 'Babylon', '4th-2nd cent BCE', 'Horowitz, W., 2014. The Three Stars Each: The Astrolabes and Related Texts (Archiv für Orientforschung, Beiheft 33). Horn/Wien: Berger & Söhne/Institut für Orientalistik der Universität Wien.', '', 'obv. 12', 531, '126', '');
INSERT INTO public.quotations VALUES (1275, 'Plenae sunt itaque is partes, in quibus decani fuerint inventi, vacuae vero, ad quas partes numquam decanorum numen accedat.', '', 945, 'Therefore, full are the degrees in which the decans are found, empty however are those to which no decan belongs', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.22.3', 1368, '1.265', '');
INSERT INTO public.quotations VALUES (375, 'GE₆ 17 DIR AN ZA ina ZALAG₂ sin ar₂ GENNA 5/6 KUŠ₃ ana ⸢NIM⸣ GUB ar₂ MUL₂ ar₂ ša₂ SUḪUR.MAŠ₂ 1 KUŠ₃ 8 u', '', 355, 'Night of (day) 17, clouds were in the sky; last part of the night, Sin stood 5/6 cubit behind Kayyamanu to the east, 1 cubit 8 fingers behind the Rear star of the Goat-Fish.', 'BM 32889+ (ADRT II No. -246)', 'astronomical diary', 'Babylon', '247 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 16', 483, '', '');
INSERT INTO public.quotations VALUES (494, '[ina] ⸢ZALAG₂⸣ sin ar₂ MUL₂ ar₂ ša₂ ALLA ša₂ SI 1 KUŠ₃', '', 455, 'Last part of the ni]ght, Sin was 1 cubit behind the rear star of the crab to the north.', 'BM 45782 (ADRT II No. -166A)', 'astronomical diary', 'Babylon', '167 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 5', 605, '484-485', '');
INSERT INTO public.quotations VALUES (395, '[GE₆ 6 SAG GE₆] ⸢sin e⸣ MUL₂ IGI ša₂ ⸢SUḪUR⸣.MAŠ₂ ⸢2 1/2⸣ KUŠ₃ SI GIN', '', 369, '[Night of (day) 6, beginning of the night,] Sin was 2 1/2 cubits above the front star of the goat-fish; the north wind blew.', 'BM 41462+ (ADRT III No. -163B)', 'astronomical diary', 'Babylon', '165 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 4', 503, '10-11', '');
INSERT INTO public.quotations VALUES (597, 'Ἥλιος, Ἀφροδίτη Ὑδροχόῳ, Σελήνη, Ζεὺς Τοξότῃ, Κρόνος Λέοντι, Ἑρμῆς Αἰγοκέρωτι, Ἄρης, ὡροσκόπος Ζυγῷ.', '', 536, 'The Sun, Venus (were) in Aquarius; the Moon, Jupiter in Sagittarius; Saturn in Leo; Mercury in Capricorn; Mars, the Ascendant in Libra.', 'Vettius Valens', 'horoscope', '', '2nd century CE', '(GH L 122,I,22)', '', '5.6.112', 712, '', '');
INSERT INTO public.quotations VALUES (393, '(6) 12 SAG GE₆ sin (7) SIG šur GIGIR ša₂ ULU₃ 1 KUŠ₃ TUR₃ NU KAD₂', '', 367, '(Day) 12, beginning of the night, Sin was 1 cubit below the southern rein of the chariot, surrounded by a halo which was not closed.', 'BM 46229 (ADRT I No. -346)', 'astronomical diary', 'Babylon', '347 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 6-7', 501, '142-143', '');
INSERT INTO public.quotations VALUES (2306, 'certa nitent mundo tum lumina: conditur omne
INSERT INTO public.quotations VALUES (392, 'GI₆ 6 SAG GI₆ sin SIG MUL₂ IGI ša₂ SAG ḪUN 4 KUŠ₃ sin 1/2 KUŠ₃ ana NIM ⸢DIB⸣', '', 242, 'Night of (day) 6, beginning of the night, Sin was 4 cubits below front star of the head of the hired man, Sin having passed 1/2 cubit to the east.', 'BM 46030 (ADRT II No. -249B)', 'astronomical diary', 'Babylon', '250 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 10''', 329, '44-45', '');
INSERT INTO public.quotations VALUES (360, 'τὸ πρῶτον τρίγωνον καὶ τετράγωνον καὶ ἑξάγωνον λέγεται τὰ εὐώνυμα †ἑξῆς ἡ Σελήνη πρῶτον συνάπτει τινὶ τῶν ἀστέρων', '', 340, 'The “first triangle” and “(first) quadrangle” and “(first) hexagon” are called the left(-aspect) ones, starting from when(?) the Moon first makes contact with any of the planets', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 461, '227', '');
INSERT INTO public.quotations VALUES (2307, 'Arctos et Orion adversis frontibus ibant,
INSERT INTO public.quotations VALUES (966, '[...] quanto vehementius pugnant quantumque persuadendi vias quaerunt, tanto fidem astrologiae vehementius fortiusque confirmant', '', 862, 'But the more aggressively they attack, the more various the ways in which they seek to convince us, the more strongly they bolster our faith in astrology.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.1.2', 9, '1.4', '');
INSERT INTO public.quotations VALUES (1042, 'qui a nobis Mars, ab illis Pyrois dicitur', '', 933, 'Mars to us is Pyrois to them', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.2.2', 1137, '1.42', '');
INSERT INTO public.quotations VALUES (2614, 'וְנָמַ֙קּוּ֙ כָּל־צְבָ֣א הַשָּׁמַ֔יִם', 'wənāmaqqû kol-ṣəb̲āʾ haššāmayim', 2279, 'And consumed have been all the host of the heavens', 'Isaiah', 'religious text', '', '', '', 'https://www.sefaria.org/Isaiah.47.13?lang=bi&with=all&lang2=en', '34 4', 2674, '', '');
INSERT INTO public.quotations VALUES (495, '[GI₆ 21 ina ZALAG₂] ⸢sin⸣ ina IGI MUL₂ MURUB₄ ša₂ SAG GIR₂.TAB 2 KUŠ₃', '', 456, '[Night of (day) 21, last part of the night], Sin was 2 cubits in front of the middle star of the head of the scorpion.', 'BM 41469 (ADRT III No. -146)', 'astronomical diary', 'Babylon', '147 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 4''', 606, '', '');
INSERT INTO public.quotations VALUES (1077, 'Africo, qui a Graecis Libs dicitur, Cancer, Scorpius, Pisces.', '', 967, 'To the south-west wind, which the Greeks call libs, are subject the signs Cancer, Scorpio, Pisces.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.12', 1172, '1.55', '');
INSERT INTO public.quotations VALUES (172, 'ὁμοροοῦν λέγεται ζῴδιον τῇ Σελήνῃ τὸ ἐπαναφερόμενον ἐφ’ ὃ φέρεται· τὸ γὰρ αὐτὸ καὶ παράκολλον καλεῖται· ἀποσαλεῦον δὲ τὸ μηδὲ ὁροούμενον ὑπὸ τῆς Σελήνης ζῴδιον', '', 178, 'A zodiac sign is said to “be a neighbor” to the Moon that is in a following position (epanapheromai) to the (sign) in which (the Moon) is: the same (sign) is called “attached”; the zodiac sign that has no contiguity(?) with the Moon is “anchored offshore”', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 256, '226', '');
INSERT INTO public.quotations VALUES (595, 'Ἥλιος, Ἀφροδίτη Ὑδροχόῳ, Σελήνη, Ζεὺς Τοξότῃ, Κρόνος Λέοντι, Ἑρμῆς Αἰγοκέρωτι, Ἄρης, ὡροσκόπος Ζυγῷ.', '', 534, 'The Sun, Venus (were) in Aquarius; the Moon, Jupiter in Sagittarius; Saturn in Leo; Mercury in Capricorn; Mars, the Ascendant in Libra.', 'Vettius Valens', 'horoscope', '', '2nd century CE', '(GH L 122,I,22)', '', '5.6.112', 710, '', '');
INSERT INTO public.quotations VALUES (376, '25 šamaš₂ GUB NU PAP', '', 355, '(Day) 25, Šamaš was at the solstice; I did not watch.', 'BM 45763 (ADRT II -256)', 'astronomical diary', 'Babylon', '257 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 17''', 482, '12-13', '');
INSERT INTO public.quotations VALUES (9, '', 'pꜣ rꜥ-ḫꜥ tꜣ ỉḫy(.t) | rꜥ-ḥtp pꜣ ỉsw', 51, 'Ascendant: Libra; Descendant: Aries', 'O. Neugebauer 3', 'horoscope', 'Thebes', 'after AD 18', 'O. Neugebauer, “Demotic Horoscopes”, JAOS 63 (1943), 117–119 (115–127).', '', '4–5', 45, '', '');
INSERT INTO public.quotations VALUES (446, '(5) [GE₆ 10] (6) [SAG] ⸢GE₆ sin SIG⸣ MUL₂ TUR ša₂ 4 [KUŠ₃] ⸢ar₂⸣ LUGAL 1 2/3 KUŠ₃ sin 8 SI ana NIM DIB', '', 412, '[Night of (day) 10, beginning] of the night, Sin was 1 2/3 cubits below the small star which is 4 cubits behind the king, Sin having passed 8 fingers to the east.', 'BM 34651 (ADRT I No. -342A)', 'astronomical diary', 'Babylon', '343 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 5-6', 555, '158-159', '');
INSERT INTO public.quotations VALUES (401, '⌈iti⌉.AB 2.20 mul2.Gu-la 1 mul2.UD.KA.DUḪ.A 30 mul2.[U]Z3', '', 374, 'Month X: 2,20 ''The Great One'', 1 ''The Demon with the Gaping Mouth'', 30 ''The [She]-Goat''.', 'BM 34713 (LBAT 1499)', 'schematic astronomy: ''three stars each'' + rising times', 'Babylon', '4th-2nd cent BCE', 'Horowitz, W., 2014. The Three Stars Each: The Astrolabes and Related Texts (Archiv für Orientforschung, Beiheft 33). Horn/Wien: Berger & Söhne/Institut für Orientalistik der Universität Wien.', '', 'obv. 10', 509, '126', '');
INSERT INTO public.quotations VALUES (517, 'DU₃-šu₂ ša₂ 2.13.20 ša₂ U₄.15.[KAM]', '', 473, 'Procedure for 2;13,20 of day 15.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. i 7', 636, '', '');
INSERT INTO public.quotations VALUES (471, 'Πυρόε[ι]ς δ’ ὁ τοῦ Ἄρεως ἀστὴ̣ρ Ὑδρο̣χόου μοίρας ἀν̣εβίβαζεν δε[κα]ὲξ εἰκοστόν', '', 433, 'The fiery one, the star of Ares (Mars) was ascending 16 degrees (and) one twentieth of Aquarius', 'P.Lond. I 130', 'horoscope', '', '81 CE', '(GH 81)', '', '126–130', 586, '', '');
INSERT INTO public.quotations VALUES (1223, 'In quadrupedibus vero signis faciet <a> bestiis comprehensos misera laceratione laniari.', '', 1097, 'In four-footed signs they will be torn apart by beasts.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.4.23', 1316, '1.121', '');
INSERT INTO public.quotations VALUES (1022, 'Horum signorum diversa sunt genera, nam alia sunt ex his masculina, alia feminina', '', 915, 'These signs are of different gender, some masculine, some feminine', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.2', 1120, '1.41', '');
INSERT INTO public.quotations VALUES (472, 'ἔστω ὑποδείγματος χάριν Ἥλιος Ταύρου μοίρᾳ αʹ, Σελήνη Διδύμων μοίρᾳ ιϛʹ, ὡροσκόπος Ταύρου μοίρᾳ ιδʹ, ἡ σύνοδος περὶ μοίρας κζʹ τοῦ Κριοῦ, ἀναβιβάζων Αἰγοκέρωτος μοίρᾳ κεʹ', '', 434, 'As an example, let the Sun be in degree 1 of Taurus, the Moon in degree 16 of Gemini, the Ascendant in degree 14 of Taurus, the conjunction (prenatal syzygy) at about 27 degrees of Aries, the ascending (node) in degree 25 of Capricorn', 'Vettius Valens', 'horoscope', '', '2nd century CE', '(GH L 74,IV)', '', '3.13.20', 587, '', '');
INSERT INTO public.quotations VALUES (2615, 'אֵ֛יךְ נָפַ֥לְתָּ מִשָּׁמַ֖יִם הֵילֵ֣ל בֶּן־שָׁ֑חַר נִגְדַּ֣עְתָּ לָאָ֔רֶץ חוֹלֵ֖שׁ עַל־גּוֹיִֽם׃', '', 2280, '', 'Isaiah', 'religious text', '', '', '', 'https://www.sefaria.org/Isaiah.47.13?lang=bi&with=all&lang2=en', '14,12', 0, '', '');
INSERT INTO public.quotations VALUES (518, '(5) [IGI.DU₈.A ša₂ d].dil-bat 8 MU.MEŠ a-na ar₂-ki-ka GUR-ar₂ 
(6) [xxxxx] 4 u₄-mu.MEŠ tu-maṭ-ṭa IGI-mar', '', 474, '(5) [The appearances ... of] Venus: you turn back 8 years behind you, (6) [...] you diminish (the date) by 4 days, you see it (Venus).', 'BM 45728', 'goal year procedure', 'Babylon', 'ca. 450-50 BCE', 'Britton chk', '', 'obv. 6', 637, '', '');
INSERT INTO public.quotations VALUES (1404, 'sed tunc malis fortioribus inplicantur, cum ad Capricornum vel ad Taurum Saturnus transitum fecerit', '', 1253, 'but they will be involved in more serious evils when Saturn moves across to Capricorn or Taurus', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '5.3.23', 1496, '2.36', '');
INSERT INTO public.quotations VALUES (808, '(33) 4 a-na 30 ŠEŠ.MEŠ SE₃-ma IGI 30 : 2
(34) 4 A.RA₂ 2 8 ⸢8⸣ A.RA₂ 4 tam-ṭi-tu₄ DU-ma
(35) 32', '', 723, '(33) You divide 4 into 30 parts: the reciprocal of 30 is (0);2, (34) 4 times 2 is (0);8. You multiply (0);8 by 4, the deficit, it is (35) (0);32.', 'BM 36722 (BMAPT No. 52)', 'mathematical astronomy: procedure text', 'Babylon', 'Seleucid', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'obv. i 33-35', 918, '', '');
INSERT INTO public.quotations VALUES (559, 'αὕτη μὲν οὖν ἐν Αἰγοκέρωτι ἐγένετο κατὰ τὸ ἀνατολικὸν κέντρον', '', 502, 'This (eclipse) happened in Capricorn at the rising center (Ascendant)', 'Marinus, Life of Proclus', 'horoscope', '', '5th cent. CE', 'GH L 412', '', '§37', 673, '', '');
INSERT INTO public.quotations VALUES (621, 'συνεγγίζει δ’ αὐτῷ ἀπλανὴς ἀστὴρ ὁ μεταξὺ τῶν χηλῶν τοῦ Σκορπίου μεγέθους δʹ ἐπέχων κατὰ μῆκος Σκορπίου δʹ μϛʹ', '', 557, 'And coming near it (the Sun) is the fixed star between the claws of Scorpius, of magnitude 4, being at a longitude of 4;46 in Scorpio', 'CCAG IV', 'horoscope', '', '', '(GH L 497)', '', 'p. 106', 736, '', '');
INSERT INTO public.quotations VALUES (2135, 'ceterum innumerabiles ferri per occultum aut propter obscuritatem luminis nobis ignotas aut propter circulorum positionem talem ut tunc demum cum ad extrema eorum venere visantur.', '', 1892, 'However, innumerable planets move in secret, unknown to us either on account of the dimness of their light or because the position of their orbits is such that they are eventually seen only when they reach the extremities of those orbits.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.255.xml', '7.13.1', 2216, '254-255', '');
INSERT INTO public.quotations VALUES (40, '', 'pꜣy-ms=w r pꜣ nṯr-tꜣy ḥr rꜥ-ḫꜥ  n ms.t n grḥ ẖn tꜣy=f ḳnḥy.t', 85, 'The one who was born when Venus was in the ascendant during a nocturnal birth being in its dignity', 'P.Carlsberg 81', 'astrological treatise', 'Tebtunis', '2nd cent. CE', '', '', 'Frag. 4, 19', 98, '', '');
INSERT INTO public.quotations VALUES (2308, 'nec lunam certos excedere luminis orbes
INSERT INTO public.quotations VALUES (377, '14 LAL₂-ti₃ NU PAP', '', 356, '(Day) 14, balancing, I did not watch.', 'BM 40574 (ADRT II -171E)', 'astronomical diary', 'Babylon', '172 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 9', 484, '444-445', '');
INSERT INTO public.quotations VALUES (567, 'in genituris cardines sunt quattuor, ortus occasus medium caelum immum caelum, quae loca a Graecis solent appellari his nominibus: anatole, dysis, mesuranima, ypogeon', '', 250, 'In nativities there are four cardinal points, Rising, Setting, Midheaven, Lower Midheaven, which places are customarily given the following names by the Greeks: Rising (ἀνατολή), Setting (δύσις), Midheaven (μεσουράνημα), Below the Earth (ὑπόγαιον)', 'Firmicus Maternus', 'astrological treatise', '', '', '', '', '2.15.1', 656, '', '');
INSERT INTO public.quotations VALUES (473, '[ὁ κλῆρος] τῆς ἀνάγκης εὑρέθη Κριῷ μοιρῶν ϛʹ οἴκῳ Ἄρεως [ὁρίοις Διός· οὗ]τος σημαίνει τὸν περὶ ἐκχθρῶν καὶ παν̣τ̣ο̣δα̣π̣οῦ [δυστυχήμα]τος', '', 435, 'The lot of necessity was found in 6 degrees of Aries, the house of Mars, terms of Jupiter. This signifies the (character) concerning enemies and misfortune of all kinds', 'P.Princ. II 75', 'horoscope', '', '138–161 CE', '(GH 138/161)', '', '8–10', 588, '', '');
INSERT INTO public.quotations VALUES (378, 'ἐπιδέκατον λέγεται τὸ δεξιὸν τετράγωνον', '', 357, '“Tenth part” is called the right quadrangle', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 487, '227', '');
INSERT INTO public.quotations VALUES (822, 'καὶ θρόνους [ἀπέ]νειμεν ἡ φύσις αὐτοῖς καὶ φυλακάς, θρόνους [μὲν] ἐφ’ ὧν ὑψοῦνται ζῳδίων καὶ δύναμ⟦η⟧ιν []λικὴν ἔχουσιν, φυλακ[ὰς δὲ] ἐφ’ ὧν ταπει[ν ][ ἐνα]ντίο[ι γεί]νονται', '', 741, 'Nature has also apportioned to (the planets) “thrones” (exaltations) and “prisons” (depressions), thrones as the zodiac signs in which they are exalted and have [...] power, and prisons as those at which they are depressed [...] and become opposite', 'P.Mich. III 149', 'astrological treatise', '', '1st/2nd cent. CE', '', '', 'xvi 23ff.', 930, '', '');
INSERT INTO public.quotations VALUES (598, 'Ἥλιος, Ἀφροδίτη Ὑδροχόῳ, Σελήνη, Ζεὺς Τοξότῃ, Κρόνος Λέοντι, Ἑρμῆς Αἰγοκέρωτι, Ἄρης, ὡροσκόπος Ζυγῷ.', '', 537, 'The Sun, Venus (were) in Aquarius; the Moon, Jupiter in Sagittarius; Saturn in Leo; Mercury in Capricorn; Mars, the Ascendant in Libra.', 'Vettius Valens', 'horoscope', '', '2nd century CE', '(GH L 122,I,22)', '', '5.6.112', 713, '', '');
INSERT INTO public.quotations VALUES (453, 'GI₆ 9 SAG GI₆ sin ina IGI GIR₃ ar₂ ša₂ A 2 KUŠ₃', '', 415, 'Night of (day) 9, beginning of the night, Sin was 2 cubits in front of the rear foot of the lion.', 'BM 45614 (ADRT III No. -165A)', 'astronomical diary', 'Babylon', '166 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 11', 563, '6-7', '');
INSERT INTO public.quotations VALUES (720, 'GAM ana tar-ṣa UŠ ana UŠ ina ḪUN 46.40.51.40', '', 641, 'The addition for the circle from the computed duration: (...)', 'BM 55545 (BMAPT No. 96)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'rev. 6', 839, '', '');
INSERT INTO public.quotations VALUES (1224, 'Sed si in horoscope <luppiter partiliter fuerit inventus, horoscopus> vero ipse sit in domo lovis [vel] Venerisque stella prospera se lovi radiatione coniungat et obstiterit Marti hac, qua diximus, radiatione grassanti, omnia haec calamitatum funera maxima parte mutantur.', '', 1098, 'If Jupiter is on the ascendant but Mars himself in the house of Jupiter, and Venus favorably aspected to Jupiter and in opposition to Mars, all these deadly calamities are to a great extent changed.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.4.25', 1317, '1.122', '');
INSERT INTO public.quotations VALUES (568, 'ŠU 1 27 na ina šamaš₂ GUB IGI AGA a-pir', '', 512, 'Month IV, (day) 1 (which followed day 30 of the preceding month), NA (= time from sunset to moonset) was 27 (UŠ), it (Sin) appeared while Šamaš was present, it was covered with a crown.', 'BM 47725 (ADRT I -381B)', 'astronomical diary', 'Babylon', '382 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'X ii 7''', 681, '82-83', '');
INSERT INTO public.quotations VALUES (527, '[Φαέ]θ̣ω̣ν ὁ τοῦ Διὸς ἐν Καρκίνωι [μοι]ρ̣ῶ̣ν ι̣θ̣ʹ ἐ̣ν̣ ἀναποδισμῶι ὁ̣ρ̣ί̣ο̣ι̣[ς] Κ̣ρ̣[όν]ο̣υ̣', '', 481, 'The shining one, the (star) of Zeus (Jupiter) (was) in Cancer, 19 degrees, in retrograde, terms of Kronos (Saturn)', 'P.Oxy. 307', 'horoscope', 'Oxyrhynchus', '46 CE', '(GH 46)', '', '8–10', 644, '', '');
INSERT INTO public.quotations VALUES (337, '(9) 52.30 ME ša₂ ME 5.4.24 GUR-ma ana ME A
(10) 1-šu ME ša₂ ME 4 ZI-ma GUR-ma UŠ ar₂', '', 321, '(9) 52;30 days it turns back (0);5,4,24 per day, then it (Saturn) rises to daylight.
(10) sixty days it moves backward (0);4 per day, then the second station.', 'AO 6477 (BMAPT No. 42)', 'mathematical astronomy: procedure text', 'Uruk', 'ca. 250-150 BCE', 'Ossendrijver 2012 No. 42', '', 'rev. 9-10', 431, '312-313', '');
INSERT INTO public.quotations VALUES (2092, 'Non religionem incutit mentibus, et quidem publice, sive deficere sol visus est, sive luna (cuius obscuratio frequentior) aut parte sui aut tota delituit?', '', 941, 'Does it not inspire religion in men’s minds, and even on a nationwide scale, if the sun is seen in eclipse, or the moon (whose eclipses are more frequent) is obscured in part or entirely?', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.143.xml', '6.3.3', 2177, '142-143', '');
INSERT INTO public.quotations VALUES (696, 'DIŠ mul.MAŠ.TAB.BA ša2 ina IGI-it mu[l.SIPA.ZI.AN.NA] GUB.MEŠ-zu d.LU2.LAL3 u d.La-[ta-ra-ak]', '', 623, '¶ The twin stars which stand opposite the [True Shepherd of Anu]: Lulal and La[tarak].', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'ii 3-4', 507, '39', '');
INSERT INTO public.quotations VALUES (61, '4 1.20.48  A', '', 75, '(Month IV day) 4, 1;20,48 Leo', 'A 3417+ (ACT No. 185)', 'mathematical astronomy: daily motion table', 'Uruk', 'ca. 188 BCE (Seleucid Era year 124)', 'Neugebauer ACT', '', 'obv. col. iv 4', 84, '', '');
INSERT INTO public.quotations VALUES (88, '10 RIN₂ 3 ša₂ al 10 RIN₂ DIRI A.RA₂ 40 DU TA 3 LA₂', '', 76, '10 Libra: 3,0, (the amount) by which it exceeds 10 Libra you multiply by 0;40, subtract from 3,0.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'Obv. i 8''', 86, '', '');
INSERT INTO public.quotations VALUES (59, '11 8.14 ⸢ALLA⸣', '', 98, '(Month X, day) 11, 8;14 Cancer', 'U 96 (ACT No. 191)', 'mathematical astronomy: daily motion table', 'Uruk', '197 BCE (Seleucid Era year 115)', 'Neugebauer ACT No. 191', '', 'obv. col. ii'' 11', 131, '', '');
INSERT INTO public.quotations VALUES (2616, '', '', 2281, '', 'Isaiah', 'religious text', '', '', '', 'https://www.sefaria.org/Isaiah.47.13?lang=bi&with=all&lang2=en', '14 12', 0, '', '');
INSERT INTO public.quotations VALUES (617, '', '[ỉw=f ḫpr r wn] sw bꜣn ṯsy | ẖn tꜣ mỉ.t 2.t r wn sw nfr ẖn nꜣ twtw.w nty hby [...', 553, '[If a] maleficent star exalts | in the 2 paths when a beneficent star is inside the zodiac signs which are depressed', 'P.Carlsberg 81', 'astrological treatise', 'Tebtunis', '2nd cent. CE', '', '', 'Frag. 31 ii 3-4', 730, '', 'Unpublished');
INSERT INTO public.quotations VALUES (1277, 'Scire debemus quod in omnibus signis et masculini generis et feminini <et masculinae> et [quae] femininae sint partes. Ex his enim partibus invenitur, cuius genitura sit, masculina an feminina.', '', 915, 'We need to know what parts of both the masculine and feminine signs are the masculine and feminine degrees, for from these degrees it is found out whether the nativity is male or female', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.23.1', 1370, '1.271', '');
INSERT INTO public.quotations VALUES (576, '', 'ḥsb.t 5.t n 22(.t) tpy ꜣḫ.t ⌈2⌉7 (wnw.t) 9.t n hrw ḥr-sꜣ-ỉs.t pa twtw pa ḥr | p(ꜣ)-rꜥ ꜣḫ.t 1⌈4⌉ sbkꜣ wꜣḥ ... …  ḥw.t | ḥsb.t 10.t mḥ-1.t | ỉꜥḥ ḏlꜣ.t 22 (ḥr-pꜣ)-štꜣ wꜣḥ | gnhḏ 12 (pꜣ)-nṯr-(dwꜣ) tbt(.w) 12 (pꜣ)-nṯr-(dwꜣ) | (ḥr-pꜣ)-kꜣ ḏlꜣ.t 8 (pꜣ)-nṯr-(dwꜣ) |(ḥr-pꜣ)-štꜣ ꜣḫ.t 3 (ḥr-pꜣ)-kꜣ wꜣḥ| (ḥr)-tšy tpy 8 (pꜣ)-nṯr-(dwꜣ) ⌈wꜣḥ⌉ | (pꜣ)-nṯr-(dwꜣ) rpy.t 12 tꜣy⸗f dny.t ḥꜥ⸗f wꜣḥ | sbkꜣ ꜣḫ.t 14 ⌈tꜣy⸗f dny.t ḥꜥ⸗f wꜣḥ (?)⌉', 521, 'Regnal year 5 of 22, Month of Thoth, day 27, 9th (hour) of the day: Harsiesis, son  of Tithoes, son of Horos: | Sun: Libra 1⌈4°⌉: (the terms of) Mercury: Adds | Opposite (?) (the) Sun … | Numbering year 10 waxing |Moon: Scorpio 22°: (the terms of) Jupiter: Adds | Cancer: 12°: (the terms of) Venus;  Pisces 12°: (the terms of) Venus | Saturn: Scorpio 8°: (the terms of) Venus: | Jupiter: Libra 3°: (the terms of) Saturn: Adds |  Mars: Aries 8°: (the terms of) Venus: ⌈Adds⌉ | Venus: Virgo 12°: its own terms: Adds
INSERT INTO public.quotations VALUES (107, '𓁶𓊪𓂝𓎡𓈖𓅓𓏏𓇼', 'tpy-ꜥ knm.t', 134, 'first one of the darkness', 'Neugebauer', 'royal tomb', 'Thebes', 'ca. 1200 BCE', '', '', '', 178, '', '');
INSERT INTO public.quotations VALUES (2617, '', '', 2282, '', 'Joshua', '', '', '', '', '', '10 11', 0, '', '');
INSERT INTO public.quotations VALUES (338, 'GU₄.UD ina ŠU₂ IGI-su DIB-iq TA 30 ABSIN EN 5 GIR₂.TAB', '', 322, 'Mercury omits its appearance in the morning from 10 Aries until 20 Taurus.', 'AO 6477 (BMAPT No. 42)', 'mathematical astronomy: procedure text', 'Uruk', 'ca. 250-150 BCE', 'Ossendrijver 2012 No. 42', '', 'obv. 9', 433, '', '');
INSERT INTO public.quotations VALUES (159, '(9'') [TA UŠ] (10'') 4 KI ana ar₂-šu₂ LA₂-is-ma ana ME E', '', 165, '(9'') [From the station] (10'') the position (of Jupiter) moves backward 4 (UŠ), then it rises to daylight (Acronychal Rising).', 'BM 34081+ (BMAPT No. 18)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 250-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'Obv. i 9''-10''', 234, '248', '');
INSERT INTO public.quotations VALUES (474, 'κλ(ῆρος) ἀνάγκης Τ̣[αύρῳ] μυρ(ῶν) ιʹ λε(πτῶν) θ̣ʹ', '', 435, 'The lot of necessity: in 10 degrees, 9 minutes of Taurus', 'PSI I 23a', 'horoscope', 'Antinoopolis', '338 CE', '(GH 338)', '', '16', 588, '', '');
INSERT INTO public.quotations VALUES (379, 'φωσφορίαι λέγονται ἐπὰν ἀποσπάσῃ ἡ Σελήνη τοῦ Ἡλίου μοίρας ιεʹ καὶ ἀνατείλῃ (...) ὁμοίως δὲ καὶ ἐπὶ τῶν ἀστέρων φωσφορίαι λέγονται', '', 358, '“Illuminations” are called when the Moon gets 15 degrees away from the Sun and has risen (...) Likewise there are also said to be “illuminations” in the case of the planets.', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 489, '227', '');
INSERT INTO public.quotations VALUES (1591, 'ὅ τε μέγας ἡγεμὼν ἡμέρας ἥλιος διττὰς καθ’ ἕκαστον ἐνιαυτὸν ἀποτελῶν ἰσημερίας, ἔαρι καὶ μετοπώρῳ, τὴν μὲν ἐαρινὴν ἐν κριῷ, τὴν δὲ μετοπωρινὴν ἐν Ζυγῷ, ἐναργεστάτην παρέχεται πίστιν τοῦ περὶ τὴν ἑβδόμην θεοπρεποῦς· ἑκατέρα γὰρ τῶν ἰσημεριῶν ἑβδόμῳ γίνεται μηνί, καθ’ ἃς καὶ ἑορτάζειν διείρηται νόμῳ τὰς μεγίστας καὶ δημοτελεστάτας ἑορτάς', '', 111, 'The Sun also, the great leader of day, by effecting twin equinoxes each year, in spring and autumn, the spring one in Aries and the autumn one in Libra, furnishes a very clear confirmation of the godly quality of the hebdomad: for each of the equinoxes occurs in a seventh month, and on them (sc. the equinoxes) it has been determined in the Law to celebrate the greatest and most sovereign festivals', 'Philo, De opificio mundi', 'philosophy', '', '', '', '', '116', 152, '', '');
INSERT INTO public.quotations VALUES (404, 'οἱ δὲ τῆς αἱρέσεως κύριοι Κρόνος καὶ Ἑρμῆς ἀγαθοδαιμονοῦντες εὑρέθησαν', '', 376, 'The lords of the sect, Saturn and Mercury, were found to be in (the place of) Agathos Daimōn', 'Vettius Valens', 'astrology', '', '2nd century CE', '(GH L 62)', '', '2.22.47', 511, '', '');
INSERT INTO public.quotations VALUES (519, 'DUR SIM.MAḪ 26?.[1]5 ŠE', '', 306, 'The ribbon of the swallow: .. 26?;[1]5 (zodiac sign corresponding to) month XII.', 'BM 36609+', 'astral compendium', 'Babylon', 'ca. 400-200 BCE', 'Roughton, N. A.; Steele, J. M.; and Walker, C. B. F. 2004  A Late Babylonian Normal and Ziqpu-Star Text. Archive for History of Exact Science 58: 537–572.', '', 'rev. iv 21''', 414, '551', '');
INSERT INTO public.quotations VALUES (11, 'οἷον ἐν Λέοντι τοῦ Ἡλίου ὄντος (...) Ζεὺς ἐκ Τοξότου τριγωνίζων', '', 58, 'such as, with the Sun being in Leo (...), Jupiter being in triangular (trine) aspect from Sagittarius', 'Antiochus, Paris Fragments', 'astrological treatise', 'Athens', '1st cent. BCE/1st cent. CE', 'CCAG VIII.3', '', '§17', 50, '115', '');
INSERT INTO public.quotations VALUES (169, 'ἰσχύοντες λέγονται ἀστέρες οἱ ἐπὶ τῶν ἐπικαίρων τῆς γενέσεως τόπων τύχοντες· τουτέστι ἐπίκεντροι· καὶ οἱ ἐπὶ φάσεως καὶ οἱ ἐπίκληροι καὶ οἱ ἐν ταῖς συνόδοις ἀκριβῶς ὄντες', '', 175, 'Those planets are said to be strong that are in the opportune places in a nativity—that is, at cardinal points—and those in phase and “heirs” (or, in lots) and those precisely in conjunction', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 252, '226', '');
INSERT INTO public.quotations VALUES (313, '', 'ḥsb.t 5.t n Ꜣntwnyns | ỉbd-3 (n) šmw sw ꜥrḳy n pꜣ Wynyn pꜣ ṯꜣy-wnw.t 3.t n grḥ', 304, 'Regnal year 5 of Antoninus (Pius) | month 3 of Shemu, day 30 according to the Greeks, 3rd hour of the night.', 'Griffith MSS 3.59', 'horoscope', 'Thebes (?)', '2nd cent. CE', 'A. Winkler, A Starry Summer Night in AD 142: A Theban Horoscope (Griffith MSS 3.59) in Context, in K. Donker van Heel, F.A.J. Hoogelijk, and C.J. Martin (eds), Hieratic, Demotic and Greek Studies and Text Editions: Of Making Many Books There Is No End. Festschrift for Sven P. Vleeming (Leiden:Brill), 298-308.', '', '1-2', 412, '301', '');
INSERT INTO public.quotations VALUES (2618, '', '', 2283, '', 'Isaiah', 'religious text', '', '', '', 'https://www.sefaria.org/Isaiah.47.13?lang=bi&with=all&lang2=en', '47 13', 0, '', '');
INSERT INTO public.quotations VALUES (927, '', 'r ḥꜣ|.ṱ⸗y šlf | n-tr.ṱ|⸗k mny sp-sn.w ỉw=y | ỉr r tw=y ḥp|ḥ(p) ỉw=y pl|ḏ ḏmꜥ ỉw=y  qty| n-ỉm=y m-sꜣ nꜣy m|šy wḥm ỉw=y | ḥpḥ(p) ỉrm (ḥr-pꜣ)-šwṱ | ḥtr.w', 813, 'I was ashamed because of you daily. I was disturbed. I unrolled (the) papyrus roll. I searched the repeated movement (of planets) and I was disturbed with Jupiter in Gemini.', 'OMM 75', 'school text', 'Narmouthis', '2nd cent. CE', 'A. Menchetti, Un aperçu des textes astrologiques de Medinet Madi, in G. Widmer and D. Devauchelle, eds., Actes du IXe Congrès International des Études Démotiques: Paris, 31 août - 3 septembre 2005; Paris, 2009, 223-241.', '', '2-12', 1012, '232-233', '');
INSERT INTO public.quotations VALUES (475, 'GE₆ 20 AN DUL ina ZALAG₂ sin ina IGI KIR₄ šil-taḫ PA 1/2 KUŠ₃', '', 437, 'Night of (day) 20, rain DUL; last part of the night, Sin was 1/2 cubit in front of the tip of Pabilsag’s arrow.', 'BM 46009 (ADRT I No. -379)', 'astronomical diary', 'Babylon', '380 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 6''', 590, '88-89', '');
INSERT INTO public.quotations VALUES (542, 'τὸ δὲ ἕκτον ἀπὸ ὡροσκόπου καλεῖται Κακὴ Τύχη καὶ ποινὴ καὶ πρόδυσις καὶ ἀπόκλιμα φαῦλον', '', 459, 'The sixth place from the Ascendant is called Bad Fortune and Punishment and Setting-in-advance and Ill Decline', 'Paul of Alexandria', '', '', '', '', '', '§24', 657, 'p. 57 Boer', '');
INSERT INTO public.quotations VALUES (640, 'ša₂ AN 4.44 MU.MEŠ ⸢2.13 x?⸣ IGI.MEŠ 2.31 BAL.MEŠ 15.6 KI DU', '', 574, 'For Mars. 4,44 years, 2,[1]3 ...? appearances, 2,31 turns, the position proceeds 15,6,(0).', 'BM 34676 (BMAPT No. 13)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'rev. 33''', 754, '232-233', '');
INSERT INTO public.quotations VALUES (570, 'DIB.MEŠ ša₂ dil-bat KI MUL₂ ŠID.MEŠ ina 12 MU-ka 4 U₄ ana MU-ka LA₂-ṭi', '', 514, 'Passages of Venus with the counting stars: in 12 years 4 days are lacking to your year.', 'BM 41004', 'mathematical astronomy: procedure text', 'Babylon', '', 'Brack-Bernsen & Hunger 2005/6, AfO 51, 96-107', '', 'rev. 5', 683, '', '');
INSERT INTO public.quotations VALUES (520, 'šur-ši ABSIN 15', '', 475, 'The root of the furrow: 15 (degrees of Virgo).', 'BM 46083', 'astral compendium', 'Babylon', 'ca. 400-50 BCE', 'Roughton, N. A.; Steele, J. M.; and Walker, C. B. F. 2004  A Late Babylonian Normal and Ziqpu-Star Text. Archive for History of Exact Science 58: 537–572.', '', 'obv. ii'' 3''', 638, '563-564', '');
INSERT INTO public.quotations VALUES (99, 'NIM u SIG ša₂ sin GAR', '', 123, 'You put down the height and depth of Sin.', 'BM 55530 (BMAPT No. 61C)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'rev. 5', 168, '', '');
INSERT INTO public.quotations VALUES (1012, 'Signa autem ipsa duodecim his nominibus nominantur: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpius, Sagittarius, Capricornus, Aquarius et Pisces.', '', 905, 'The twelve signs themselves are called by these names: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpio, Sagittarius, Capricorn, Aquarius, and Pisces.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.2', 1110, '1.41', '');
INSERT INTO public.quotations VALUES (993, 'Haec omnia mathematicorum collegit ingenium et, computatione sollerti, divinae dispositionis secreta perdidicit.', '', 888, 'Astrologers have traced all these phenomena and with their mathematical skill have taught us their secrets.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.4.11', 1085, '1.13', '');
INSERT INTO public.quotations VALUES (1237, 'Quodsi deficiens lumine in hoc loco constituta in diurna genitura ad Solem feratur, processu temporis potentiam gloriam administrationesque largitur.', '', 1110, 'If [the moon] is found waning in this place in a day-chart moving toward the sun, in the course of time [the native] achieves power, glory and high positions.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.14.8', 1330, '1.193', '');
INSERT INTO public.quotations VALUES (204, 'ša₂ GU₄.UD IGI ša₂ ŠU₂ ana IGI ša₂ ŠU₂ TA 6 ALLA EN 26 RIN₂ 2.40 TAB', '', 214, 'For Mercury: western appearance to western appearance from 6 Cancer until 26 Libra you add 2,40.', 'AO 6477 (BMAPT No. 42)', 'mathematical astronomy: procedure text', 'Uruk', 'ca. 200 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'obv. 11', 1103, '', '');
INSERT INTO public.quotations VALUES (1313, 'Si enim generaliter fuerit inventus in his, in quibus diximus, locis, mediam genituram decernit', '', 1168, 'If it is roughly found in the place we said, it indicates a mediocre nativity', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.17.9', 1407, '1.240-241', '');
INSERT INTO public.quotations VALUES (994, 'nunc directo cursu, nunc retrogrado, nunc stativa tarditate subsistunt et intentionem quaerentium ex ista cursus sui varietate confundunt', '', 889, 'sometimes in directmotion, sometimes retrograde, sometimes sluggish (they always confuse the researcher by the variety of their courses)', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.4.11', 1086, '1.14', '');
INSERT INTO public.quotations VALUES (635, 'MUL₂ 2 ša₂ GIŠ.KUN-šu₂ [...]', '', 571, 'Two stars of his rump [...]', 'BM 36628+', 'astral compendium', 'Babylon', 'ca. 450-50 BCE', 'Steele, J. 2015, A Late Babylonian Compendium of Calendrical and Stellar Astrology, Journal of Cuneiform Studies 67, 187-215', 'http://10.5615/jcunestud.67.2015.0187', 'obv. iii 11''', 750, '196, 202', '');
INSERT INTO public.quotations VALUES (238, '16 BAR 3 SIG MUL₂ ar₂ ša₂ MUL₂.MUL₂ 1 1/2 KUŠ₃', '', 247, '(Year) 16, month I, (day) 3, it (Venus) was 1 1/2 cubits below the rear star of the bristle.', 'BM 36751+ (ADRT V No. 61)', 'astronomical diaries and related texts: planetary excerpt', 'Babylon', 'ca. 280-320 BCE', 'Hunger, H. 2001. Astronomical Diaries and Related Texts from Babylonia, Vol. V. Lunar and Planetary Texts. Vienna, Austria: Verlag der Österreichischen Akademie der Wis­senschaften.', '', 'rev. iii 11', 332, '230-231', '');
INSERT INTO public.quotations VALUES (223, '', 'pꜣy-ms=w r pꜣ nṯr-t(w)ꜣ n rꜥ-ḥtp ỉw=f ꜥšꜣ wꜥw ỉrm s-ḥm.t', 231, 'The one who was born when Venus was in the descendant, he wỉll be in a lot of strife with a woman.', 'P.Berlin 8345', 'astrological treatise', 'Soknopaiou Nesos', '1st cent. CE to 2nd cent. CE', 'Quack, J. F., 2008, Texte aus Ägypten: Demotische, magische und divinatorische Texte. In B. Janowski and B. Wilhelm (eds), Omina, Orakel, Rituale und Beschwörungen. Texte aus der Umwelt des Alten Testaments, Neue Folge 4. Gütersloh: Gütersloher Verlagshaus.', '', '', 317, '', '');
INSERT INTO public.quotations VALUES (340, 'GI6 19 19 DIRI AN DIB ULU₃ ŠAR₂', '', 322, 'Night of (day) 19, (daytime of day) 19: clouds crossed the sky, gusty wind.', 'BM 34651 (ADRT I No. -342A)', 'astronomical diary', 'Babylon', '343 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 11', 434, '158-159', '');
INSERT INTO public.quotations VALUES (333, '', '⌈wpy⌉ nꜣ sw.w bꜣn.w | ỉrm nꜣ sw.w nfr.w ḥr-pꜣ-kꜣ ḥr-tšy pꜣ-rꜥ nꜣ sw.w bꜣn.w nꜣ.w ḥr-pꜣ-šty nṯr-tꜣy sbkꜣ nꜣ sw.w nfr.w nꜣ.w', 319, 'Listing the malefic stars | and the benefic stars: Saturn, Mars, and the Sun are the malefic stars; Jupiter, Venus, and Mercury are the benefic stars.', 'P.Carlsberg 81', 'astrological treatise', 'Tebtunis', '2nd cent. CE', 'Winkler, A. 2016. Some Astrologers and their Handbooks in Demotic Egyptian. In The Circulation of Astronomical Knowledge in the Ancient World, ed. J. M. Steele. Leiden: Brill: ###', '', '', 427, '258', '');
INSERT INTO public.quotations VALUES (396, '', 'nꜣ sḫny(.w) n pꜣ-nṯr-tꜣy | pꜣy-ms=w r pꜣ-nṯr-tꜣy n rꜥ-ḫꜥ ...', 370, 'The influences of Venus | The one who was born when Venus was in the ascendant ...', 'P.Berlin 8345', 'astrological treatise', 'Soknopaiou Nesos', '1st cent. CE to 2nd cent. CE', 'Quack, J. F., 2008, Texte aus Ägypten: Demotische, magische und divinatorische Texte. In B. Janowski and B. Wilhelm (eds), Omina, Orakel, Rituale und Beschwörungen. Texte aus der Umwelt des Alten Testaments, Neue Folge 4. Gütersloh: Gütersloher Verlagshaus.', '', 'i 1-2', 504, '', '');
INSERT INTO public.quotations VALUES (406, 'ἐπεὶ οὖν νυκτερινὴ ἡ γένεσις ζητῶ Σελήνην· αὕτη τυγχάνει Καρκίνῳ τριγώνῳ Ἄρεως· τὸν δὲ τοῦ Ἄρεως εὕρομεν ἐπαναφερόμενον ἰδίῳ οἴκῳ καὶ τριγώνῳ καὶ αἱρέσει ἰδίᾳ', '', 378, 'Now, since the nativity is nocturnal, I look for the Moon: it is in Cancer, the triangle of Mars, and we found Mars in a succedent place in its own house and triangle and its own sect', 'Vettius Valens', 'astrology', '', '2nd century CE', '(GH 50)', '', '2.22.3–4', 516, '', '');
INSERT INTO public.quotations VALUES (929, 'ina ni-ip-ṣu ša2 d.SAG.ME.GAR ina mul2.UR.A AN ina mul2.GU ŠEG3 MAḪ | ina mul2.KUN u3 mul2.ABSIN KIMIN', '', 812, 'At the nipṣu of Jupiter in Leo, and Mars in Aquarius: heavy rain. In Pisces and Virgo: dito.', 'AO 6449 (TCL 6 19 = TU 19)', 'astrological weather prediction', 'Uruk', '226 BCE (12 V Seleucid Era 84)', 'Hunger, H. 1976. Astrologische Wettervorhersagen. Zeitschrift fur Assyriologie 66: 234–260', '', 'rev. 1-2', 1014, '248', '');
INSERT INTO public.quotations VALUES (499, 'Καὶ ὅταν προδύνῃ τοῦ ἡλίου καὶ ὅταν ἐπικαταδύνῃ τῷ ἡλίῷ, ἐπέστραπται τὸ λαμπρὸν πρὸς τὸν ἥλιον.', '', 458, 'And when it (i.e. the Moon) sets before the sun, and when it sets after the sun, the bright [side] is turned towards the sun.', 'Geminus', 'astronomy', '', '1st century BCE', 'Geminus. 1898. Γεμίνου Εἰσαγωγή εἰς τὰ Φαινόμενα, Gemini Elementa Astronomiae, ed. C.H.A. Manitius. Leipzig: B.G. Teubner.', 'http://www.wilbourhall.org/pdfs/GeminusElementaAstronomiae.pdf', '9.1', 610, '124', '');
INSERT INTO public.quotations VALUES (671, 'DIŠ MUL ša2 ina 15-šu2 GUB-zu mul.Nin-maḫ', '', 603, '¶ The star which stands at its right: Ninmaḫ.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'ii 21', 792, '43', '');
INSERT INTO public.quotations VALUES (2378, 'ut, quos subsequitur Cancer per sidera fratres,
INSERT INTO public.quotations VALUES (591, 'Ἥλιος, Ἀφροδίτη Ὑδροχόῳ, Σελήνη, Ζεὺς Τοξότῃ, Κρόνος Λέοντι, Ἑρμῆς Αἰγοκέρωτι, Ἄρης, ὡροσκόπος Ζυγῷ. τῷ λεʹ ἔτει ἐκινδύνευσε συσχεθῆναι διὰ στάσιν καὶ βίαν· εἶχε γὰρ καὶ ἡ Σελήνη ἑξάγωνον σχῆμα πρὸς Ἄρεα, καὶ αὐτὸς Ἄρης παρειληφὼς παρ’ αὐτῆς τὸν ἐνιαυτὸν καὶ Κρόνῳ παραδεδωκώς· αὗται οὖν αἱ ἀντιπαραδόσεις χαλεπαὶ καὶ ἐπιτάραχοι.', '', 530, 'Sun, Venus in Aquarius; Moon, Jupiter in Sagittarius; Saturn in Leo; Mercury in Capricorn; Mars, the Ascendant in Libra. In his 35th year (the native) was at risk of being imprisoned on account of sedition and violence: for the Moon had a sextile configuration in relation to Mars, and Mars itself had taken over the year from it and handed (it) over to Saturn. These transfers (of the position of chronokrator) are difficult and disruptive.', 'Vettius Valens', 'horoscope', '', '2nd century CE', '(GH L 122,I,22)', '', '5.6.112–113', 706, '', '');
INSERT INTO public.quotations VALUES (407, '(9'') GISKIM.BI ZU.ŠE₃ 59.8.58.16.17.46.40 a-na 6.4.30 IL₂-ma (10'') 5.59.20 ip-pal-ka', '', 380, 'In order to know its sign: you ‘raise’ (0);59,8,58,16,17,46,40 to 6,4;30, then (10’) it gives you 5,59;20.', 'BM 36712 (BMAPT No. 102)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'obv. 9''-10''', 517, '518-519', '');
INSERT INTO public.quotations VALUES (455, 'GI₆ 4 SAG GI₆ sin ina IGI RIN₂ ša₂ ULU₃ 2 1/2 KUŠ₃', '', 417, 'Night of the 4th, beginning of the night, the moon was 2 1/2 cubits in front of the southern (part of) the scales.', 'BM 36388+ (ADRT I No. -332A)', 'astronomical diary', 'Babylon', '333 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 3', 565, '168-169', '');
INSERT INTO public.quotations VALUES (616, '', '[ỉw=f ḫpr r wn] sw bꜣn ṯsy | ẖn tꜣ mỉ.t 2.t r wn sw nfr ẖn nꜣ twtw.w nty hby [...', 552, '[If a] malefic star exalts | in the 2 paths when a benefic star is inside the zodiac signs which are depressed [...', 'P.Carlsberg 81', 'astrological treatise', 'Tebtunis', '2nd century CE', '', '', 'Frag. 31 ii 3-4', 729, '', 'Unpublished text.');
INSERT INTO public.quotations VALUES (425, 'DIŠ MUL ša2 ina IGI mul.UZ3 GUB-zu mul.UR.GI7', '', 373, '¶ The star which stands in front of the Goat: the Dog.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'i 25', 508, '35', '');
INSERT INTO public.quotations VALUES (188, 'δορυφόροι ἀστέρες λέγονται ἑῷοι μὲν ⟨οἱ⟩ προαναφερόμενοι Ἡλίῳ, ἑσπέριοι δὲ οἱ προανατέλλοντες μὲν Ἡλίῳ, τῇ δὲ Σελήνῃ ἐπαναφερόμενοι', '', 193, '“Bodyguards” are called planets at dawn (or, in the east) when they rise before the Sun, and at evening (or, in the west) when they rise before the Sun and rise following the Moon', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 281, '227', '');
INSERT INTO public.quotations VALUES (190, 'αἱ πρὸ Ἡλίου ιεʹ ⟨μοῖραι⟩ εὐσεβῶν λέγονται, ὡς ἀπολελυμέναι ἤδη τῶν τοῦ Ἡλίου αὐγῶν· αἱ δὲ μεθ’ Ἡλίου μοῖραι ιεʹ ἀσεβῶν, ὅτι οἱ ἐκεῖ εὑρισκόμενοι ἀστέρες ἀδρανέστεροί εἰσιν ὑποπεπτωκότες ταῖς Ἡλίου αὐγαῖς', '', 194, 'The 15 <degrees> before the Sun are called those “of the pious,” as they are already “discharged from the service of” the rays of the Sun, and the 15 degrees after the Sun, those “of the impious,” because the planets found there are weaker, having “fallen at the feet” of the rays of the Sun.', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 283, '227', '');
INSERT INTO public.quotations VALUES (1402, 'Sed et in Pisces et in Geminos cum circumiens venerit, haec eadem decernit quae in tropicis.', '', 1251, 'But when he—going around—enters Pisces and Gemini, he indicates the same things as he did in the tropical signs.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '5.3.20', 1494, '2.35', '');
INSERT INTO public.quotations VALUES (1013, 'Signa autem ipsa duodecim his nominibus nominantur: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpius, Sagittarius, Capricornus, Aquarius et Pisces.', '', 906, 'The twelve signs themselves are called by these names: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpio, Sagittarius, Capricorn, Aquarius, and Pisces.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.2', 1111, '1.41', '');
INSERT INTO public.quotations VALUES (930, '', 'ỉn tꜣy πυναγίς | ꜥꜣ(.t) ỉmy ỉrm tꜣy wpe | ꜥꜣ(.t) {ỉrm} ḥsb.t 3.t n Πετοσῖρις', 815, 'Bring the great pinax | quickly and the great list | of year 3 of Petosiris', 'OMM 423', 'school text', 'Narmouthis', '2nd cent. CE', 'A. Menchetti, Un aperçu des textes astrologiques de Medinet Madi, in G. Widmer and D. Devauchelle, eds., Actes du IXe Congrès International des Études Démotiques: Paris, 31 août - 3 septembre 2005; Paris, 2009, 223-241.', '', '1-4', 1015, '231-232', '');
INSERT INTO public.quotations VALUES (1329, 'Nechepso, iustissimus Aegypti imperator et astrologus valde bonus', '', 1106, 'Nechepso, the most just emperor of Egypt and a very good astronomer/astrologer', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.22.2', 1421, '1.265', '');
INSERT INTO public.quotations VALUES (1104, 'quod a Graecis diametrum dyticon dicitur', '', 991, 'which is called western opposite by the Greeks', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.15.2', 1201, '1.58', '');
INSERT INTO public.quotations VALUES (2309, 'omnia concordi tractu veniuntque caduntque', '', 1251, 'They all move on a consistent course, coming into view and setting', 'Manilius, Astronomica', 'astrological treatise', '', '', 'Manilius. Astronomica. Edited and translated by G. P. Goold. Loeb Classical Library 469. Cambridge, MA: Harvard University Press, 1977.', 'https://www.loebclassics.com/view/manilius-astronomica/1977/pb_LCL469.47.xml', '1.537', 2378, '46-47', '');
INSERT INTO public.quotations VALUES (478, 'GE₆ 6 SAG GE₆ sin ar₂ MUL₂ KUR ša₂ KIR₄ šil-taḫ PA 1 2/3 KUŠ₃', '', 437, 'Night of (day) 6, beginning of the night, Sin was 1 2/3 cubits behind the bright star of the tip of Pabilsag’s arrow.', '', '', '', '', '', '', '', 590, '', '');
INSERT INTO public.quotations VALUES (342, 'a-na 1 DANNA 8 u NIM u SIG', '', 323, 'For 1 bēru (of distance along the ecliptic) it (Jupiter) ascends and descends 8 fingers.', 'BM 37266 (BMAPT No. 47)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. 4', 435, '334', '');
INSERT INTO public.quotations VALUES (485, 'Ἦμος δ᾽ ἠέλιος κατέδυ καὶ ἐπὶ κνέφας ἦλθε,
INSERT INTO public.quotations VALUES (560, 'ἔθηκα τὰ ἐλάχιστα τῆς Σελήνης κεʹ ἔτη, καὶ τοῦ Καρκίνου τὴν ἀναφορὰν τοῦ δευτέρου κλίματος ἔτη λβʹ, καὶ τοῦ οἰκοδεσπότου τῆς Σελήνης, Διός, ἔτη ιβʹ· γίνονται ἐπὶ τὸ αὐτὸ ἔτη ξθʹ. τοσούτων ἐτῶν ἐτελεύτα.', '', 503, 'I added the minimum years of the Moon, 25, and the rising-time of Cancer for the second klima, 32 years, and of Jupiter, the house-master of the Moon, 12 years: they total 69 years. That was his at death.', 'Vettius Valens', 'horoscope', '', '2nd century CE', '(GH L 75)', '', '3.11.15–16', 674, '', '');
INSERT INTO public.quotations VALUES (1014, 'Signa autem ipsa duodecim his nominibus nominantur: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpius, Sagittarius, Capricornus, Aquarius et Pisces.', '', 907, 'The twelve signs themselves are called by these names: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpio, Sagittarius, Capricorn, Aquarius, and Pisces.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.2', 1112, '1.41', '');
INSERT INTO public.quotations VALUES (316, '[... GU₄].UD ŠU₂ <ina> ZALAG₂-ru GENNA E-a IB₂.TAG₄ dUDU.IDIM.MEŠ NU GUB.MEŠ 3 KUŠ₃ ⸢ar₂⸣ MUL₂ TUR ša₂ 2 1/2 KUŠ₃ ⸢ar₂⸣ [MUL₂-ar₂-ša₂-SUḪUR-MAŠ₂ a-dir ...]', '', 307, '[... Mer]cury set; <during> clearing, Saturn came out; the remainder of the planets did not stand there; 3 cubits behind the small star which is 2 1/2 cubits behind [the rear star of the goat-fish it was eclipsed ...]', 'BM 33044+33047 (ADRT III No. -122D)', 'astronomical diary', 'Babylon', '123 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 10''', 415, '', '');
INSERT INTO public.quotations VALUES (383, '', 'mỉ ỉr=s m-ḳd n pꜣ ḫpš m-sꜣ tꜣ ryr.t', 360, 'Cause that she makes her travel to Ursa major and the Sow.', 'GEMF 16', 'magical text', 'Thebes', '2nd cent. CE', 'Faraone, Ch. and Torallas Tovar, S. 2022, Greek and Egyptian Magical Formularies: Text and Translation, vol. I (Berkeley CA)', '', 'xxi 38', 493, '', '');
INSERT INTO public.quotations VALUES (767, 'Κρόνον Κρειῷ ι̣αʹ λε̣[π](τῶν) λ̣β̣ʹ ἀκρώνυκτον ἀφαιρετι[κό]ς', '', 684, 'Saturn in 11 (degrees) 32 minutes of Aries, acronychal, in retrograde', 'P.Oxy. 1476', '', '', '', '(GH 260)', '', '5–6', 877, '', '');
INSERT INTO public.quotations VALUES (502, 'Καὶ ὅταν προδύνῃ τοῦ ἡλίου καὶ ὅταν ἐπικαταδύνῃ τῷ ἡλίῷ, ἐπέστραπται τὸ λαμπρὸν πρὸς τὸν ἥλιον.', '', 460, 'And when it (i.e. the Moon) sets before the sun, and when it sets after the sun, the bright [side] is turned towards the sun.', 'Geminus', 'astronomy', '', '1st century BCE', 'Geminus. 1898. Γεμίνου Εἰσαγωγή εἰς τὰ Φαινόμενα, Gemini Elementa Astronomiae, ed. C.H.A. Manitius. Leipzig: B.G. Teubner.', 'http://www.wilbourhall.org/pdfs/GeminusElementaAstronomiae.pdf', '9.1', 612, '124', '');
INSERT INTO public.quotations VALUES (1557, 'Locum desideriorum si partiliter volueris invenire, in diurna quidem genitura a parte Daemonis usque ad partem Fortunae conputa', '', 1381, 'If you want to find the place of longing by degree in a day chart, count from the degree of the [lot of] spirit until the degree of [the lot of] fortune', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.32.45', 1649, '2.187', '');
INSERT INTO public.quotations VALUES (334, '', '⌈wpy⌉ nꜣ sw.w bꜣn.w | ỉrm nꜣ sw.w nfr.w ḥr-pꜣ-kꜣ ḥr-tšy pꜣ-rꜥ nꜣ sw.w bꜣn.w nꜣ.w ḥr-pꜣ-šty nṯr-tꜣy sbkꜣ nꜣ sw.w nfr.w nꜣ.w', 320, 'Listing the maleficent stars | and the beneficent stars: Saturn, Mars, and the Sun are the maleficent stars; Jupiter, Venus, and Mercury are the beneficent stars.', 'P.Carlsberg 81', 'astrological treatise', 'Tebtunis', '2nd cent. CE', 'A. Winkler, 2016, Some Astrologers and Their Handbooks in Demotic Egyptian, in J.M. Steele (ed.), The Circulation of Astronomical Knowledge in the Ancient World (Leiden: Brill), 245-286', '', '', 428, '258', '');
INSERT INTO public.quotations VALUES (408, '15 ina ZALAG₂ sin⸣ ina IGI šur GIGIR ša₂ SI 2 ⸢KUŠ₃⸣', '', 381, 'Night of (day) 15, last part of the night, Sin was 2 cubits in front of the northern rein of the chariot.', 'BM 34792 (ADRT I No. -373B)', 'astronomical diary', 'Babylon', '374 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 3''', 518, '104-105', '');
INSERT INTO public.quotations VALUES (343, '', 'rḫ-p.t ꜥšꜣ-ỉḫy | ms ꜣs.t-wry.t', 324, 'The knower of heaven, Ashaihy | born of Esoeris.', 'Sotheby''s 14.6.2000', 'inscription', 'Egypt', '300 BCE - 100 BCE', 'Depauw, M., 2001, A Demotic Stela of an Astronomer, Enchoria 27, 1-2.', '', '1-2', 438, '1-2', '');
