INSERT INTO public.quotations VALUES (562, '33 MUL.MEŠ šu-ut d.En-lil2', '', 507, '33 stars of Enlil.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'i 39', 676, '38', '');
INSERT INTO public.quotations VALUES (1089, 'fratrum in tertio', '', 978, 'the [place] of the brothers [is] in the third [sign]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.14.3', 1186, '1.58', '');
