INSERT INTO public.quotations VALUES (760, '1-en SAG.KI GU₄ ša₂ 12 SAG [GAL]', '', 677, 'One trapezoid of which the large side is (0);12.', 'BM 34081+ (BMAPT No. 18)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 250-50 BCE', 'Ossendrijver, M. 2016, Ancient Babylonian astronomers calculated Jupiters position from the area under a time-velocity graph, Science, 351, 482-484', 'http://dx.doi.org/10.1126/science.aad8085', 'obv. i 20''', 870, '', '');
INSERT INTO public.quotations VALUES (503, 'ana tar-ṣa qup-pu IGI-u₂ ša₂ GU KA×MI', '', 461, 'Opposite the front basket of the great one it was eclipsed.', 'BM 33024+ (ADRT III No. -123A)', 'astronomical diary', 'Babylon', '124 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 15''', 613, '284-285', '');
INSERT INTO public.quotations VALUES (27, 'ana bu-lim ina ŠA3 mul.SUḪUR.MAŠ2 mul.AL.LUL u mul.GU4.AN.NA', '', 71, 'For cattle: in the Goat-Fish, the Crab, and the Bull of Heaven (or: Jaw of the Bull)', 'BM 47494', 'astrological compendium', 'Babylon?', '464-337 BCE', 'Hunger, H. 2004. Stars, Cities, and Predictions. Pp. 16–32 in Studies in the History of the Exact Sciences in Honour of David Pingree, eds. Charles Burnett, Jan Hogendijk, Kim Plofker and Michio Yano. Leiden: Brill.', '', 'obv. 22', 92, '', '');
INSERT INTO public.quotations VALUES (457, 'ina ZALAG₂ GU₄.UD SIG RIN₂ ša₂ SI 2 SI', '', 418, 'Last part of the night, Mercury was 2 fingers below the northern (part of) the scales.', 'BM 34609 (ADRT III No. -144)', 'astronomical diary', 'Babylon', '145 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 4', 566, '92-93', '');
INSERT INTO public.quotations VALUES (971, 'facit [...] Venus luxuriosos, venustos et honesto gratiae splendore fulgentes', '', 867, 'Venus [makes men] pleasure-loving, charming, handsome', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.2.2', 1062, '1.6', '');
INSERT INTO public.quotations VALUES (531, '', '7   sw 1   ḏlꜣ.t   7 0 [5]⌈7⌉ ꜥḥꜥ mḥ-1', 467, '7   day 1   Scorpio    7 0 [5]⌈7⌉ First station', 'P.Monts.Roca. inv. 314', 'sign entry table', 'Egypt', '2nd cent. CE', 'M. Escolano-Poveda, 2018/2019, Astronomica Monserratensia I: A Demotic monthly almanac with synodic phenomena (P.Monts.Roca. inv. 314), Enchoria 36, 1-36.', '', 'ii x+9', 648, '4;6', '');
INSERT INTO public.quotations VALUES (384, '', 'mỉ ỉr=s m-ḳd n pꜣ ḫpš m-sꜣ tꜣ ryr.t', 361, 'Cause that she makes her travel to Ursa Major and the Sow.', 'GEMF 16', 'magical text', 'Thebes', '2nd cent. CE', 'Faraone, Ch. and Torallas Tovar, S. 2022, Greek and Egyptian Magical Formularies: Text and Translation, vol. I (Berkeley CA)', '', 'xxi 38', 494, '', '');
INSERT INTO public.quotations VALUES (1772, 'in cauda vero sola pars XXX. reperitur.', '', 1577, 'in the tail [of Taurus] the 30th degree is located', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.4.2', 1863, '2.288', '');
INSERT INTO public.quotations VALUES (144, 'χρηματίζοντες ⟨ἀστέρες⟩ λέγονται οἱ ἐπὶ δυνάμεως ἐπίκεντροι ἢ ἐπίκληροι καὶ οἱ ἐπὶ φάσεως ἢ οἱ τρίγωνοι Ἡλίου γινόμενοι. συγχρηματίζοντες δὲ οἱ κοινῶς ἔγκεντροι ἢ ἔγκληροι καὶ οἱ ἐπὶ φάσεως. ἀχρημάτιστοι δέ εἰσιν οἱ μὴ οὕτως ἔχοντες', '', 154, 'Stars that are active are called those in force (and) in cardinal points, or “heirs” (or, in lots), and those in phase, or those in trine with the Sun. (Stars) that are jointly active are those generally at cardinal points, or “heirs,” and those in phase. Inactive are those that are not in this disposition', 'Sarapion of Alexandria, Various Names for Configurations of the Stars', 'astrology', '', '1st cent. BCE/CE?', 'CCAG VIII.4', '', '', 215, '225', '');
INSERT INTO public.quotations VALUES (409, '⸢USAN⸣ MUL₂.BABBAR SIG šur SI 2 1/2 KUŠ₃', '', 381, 'First part of the night, Jupiter was 2 1/2 cubits below the northern rein (of the chariot).', 'BM 45722+ (ADRT II No. -182C)', 'astronomical diary', 'Babylon', '183 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 17', 518, '', '');
INSERT INTO public.quotations VALUES (1786, 'III. et IV. et V. in collo', '', 1588, '[the degrees] 3 and 4 and 5 [are located] in the neck [of the Crab]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.4.4', 1878, '2.289', '');
INSERT INTO public.quotations VALUES (641, '', 'ỉw⸗f ḫpr r ⌈wꜥ⌉ mꜣꜥ ỉw⸗f [h]⌈by⌉ pꜣy', 575, 'If it is a depressed place', 'P.Carlsberg 81', 'astrological treatise', 'Tebtunis', '2nd cent. CE', '', '', 'Frag. 5 i 6', 755, '', 'Unpublished');
INSERT INTO public.quotations VALUES (532, '', 'mtw=k ꜥš nꜣy r-r=f n sp 7 n hrw 7 wbꜣ tꜣ pr(.t) n pꜣ rꜥ', 482, 'And you recite this towards him seven times for seven days opposite the appearance of the Sun.', 'GEMF 16', 'magical text', 'Thebes', '2nd cent. CE', 'Faraone, Ch. and Torallas Tovar, S. 2022, Greek and Egyptian Magical Formularies: Text and Translation, vol. I (Berkeley CA)', '', '364', 649, '182', '');
INSERT INTO public.quotations VALUES (224, '[ŠU₂ IGI u] ⸢UŠ⸣ ša₂ ⸢KUR⸣ ana ŠU₂ IGI u UŠ ša₂ KUR 9.35.30 KI TAB', '', 232, '[Setting, appearance and] station of the east to setting, appearance and station of the east: you add 9,35,30 (to) the position.', 'BM 34221 (BMAPT No. 46)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'rev. ii 9', 318, '328-329', '');
INSERT INTO public.quotations VALUES (1320, 'quia post Arietem ad Taurum secundo loco Luna transitum facit, Venus geniturae dominium possidebit', '', 1172, 'because after Aries the Moon moves into Taurus in the second place, Venus [i. e. the ruler of Taurus] will possess rulership of the chart', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.19.4', 1411, '1.244', '');
INSERT INTO public.quotations VALUES (1297, 'Sed semper Luna in omni orbis sui figuratione si feratur ad Venerem, faciet praeposteris amorum vitiis implicates', '', 1155, 'But always if the Moon—in any phase—moves towards Venus it will make extraordinary sexual vices and constant scandal', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.6.3', 1390, '1.204', '');
INSERT INTO public.quotations VALUES (504, '[...] KUŠ₃ ar₂ qup-pu ar₂ ša₂ GU a-dir', '', 462, '[...] cubits behind the rear container of the great one it was eclipsed.', 'BM 32149+ (ADRT I No. -366B)', 'astronomical diary', 'Babylon', '367 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 6', 614, '134-135', '');
INSERT INTO public.quotations VALUES (385, '', 'tw=w šm pꜣy=k by ỉrm swḥ r-tw wsỉr pꜣy ỉrm nꜣ sỉw.w nty šms n spd.t', 362, 'Cause that your ba-sould goes with Orion, who is Osiris, and the stars, which follow Sothis.', 'P.Rhind I', 'religious text', 'Thebes', '1st cent. BCE', 'G. Moller, 1913, Die beiden Totenpapyrus Rhind des Museum su Edinburg (Leipzig)', '', 'vi 5d', 495, '', '');
