INSERT INTO public.quotations VALUES (1074, '[...] quoto anno unumquodque signum in genituris oriatur', '', 964, '[...] after how many years each particular sign rises in the charts', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.11.1', 1169, '1.54', '');
