INSERT INTO public.quotations VALUES (596, 'Ἥλιος, Ἀφροδίτη Ὑδροχόῳ, Σελήνη, Ζεὺς Τοξότῃ, Κρόνος Λέοντι, Ἑρμῆς Αἰγοκέρωτι, Ἄρης, ὡροσκόπος Ζυγῷ.', '', 535, 'The Sun, Venus (were) in Aquarius; the Moon, Jupiter in Sagittarius; Saturn in Leo; Mercury in Capricorn; Mars, the Ascendant in Libra.', 'Vettius Valens', 'astrological treatise', '', '2nd century CE', '(GH L 122,I,22)', '', '5.6.112', 711, '', '');
INSERT INTO public.quotations VALUES (703, 'DIŠ mul.d.MUŠ d.Nin-giš-zi-da EN er-ṣe-tu4', '', 329, '¶ The Snake, Ningišzida, lord of the Netherworld.', 'BM 86378', 'schematic astronomy: mulapin', '?', 'ca. 626-539 BCE', 'Hunger, H. and Steele, J., 2019. The Babylonian Astronomical Compendium MUL.APIN. London and New York: Routledge.', '', 'ii 8', 444, '40', '');
INSERT INTO public.quotations VALUES (668, 'mul.GAM3 mul.LUGAL mul.U5.RI2.IN mul.ZU2.MUŠ.I3.NAG.A mul.is le-e mul.MUŠ mul.Li9-si4 7 ti-ik-pi', '', 600, 'Crook-star, King-star, Eagle-star, Deleter-star, Jaw-of-the-Bull-star, Snake-star, Lisi star: 7 dot stars.', 'K 250+ (CT 26 40-41)', 'star list', 'Nineveh', '7th cent BCE', 'Koch-Westenholz, U., 1995. Mesopotamian Astrology. An Introduction to Babylonian and Assyrian Celestial Divination. Copenhagen: Museum Tusculanum Press.', '', 'rev. v 10-13', 788, '198', '');
INSERT INTO public.quotations VALUES (692, 'Πυρόεις Ἡρακλ[έους]', '', 619, 'The Fiery one (Mars), (the star) of Herakles', 'IGLS I 36', 'horoscope', '', '62 BCE?', '(GH –61)', '', '', 814, '', '');
INSERT INTO public.quotations VALUES (735, '', '...] tkn syw pꜣy=f ỉry ḥr tꜣ mỉ.t n rhwy šby nꜣ ḫꜥ.w n spt.t ỉr ỉꜥḥ ...', 552, '...] a star pushed away its other on the way of the evening. The appearances of Sothis were changed. The Moon ...', 'P.Carlsberg XI 5', 'literary text', 'Tebtunis', '1st - 2nd century CE', 'Quack, J. F., 2019, Isis, Thot und Arian aud der Suche nach Osiris, in J. F. Quack and K. Ryholt, eds.,  Demotic Literary Texts from Tebtunis and Beyond, Copenhagen 2019, 77-138.', '', 'Frag. 1, x+17', 729, '80-81', '');
INSERT INTO public.quotations VALUES (2224, '...]ⲱⲡ ϧⲛⲟⲩ ⲁϭⲏⲗⲱ ⲁⲟⲩⲁⲣⲕ̅ⲇ̅ ⲻⲃⲱⲛ  ⲁⲣ[ⲁ]ϥ', '...]ôp xnou acêlô aouar24-bôn ar[a]f', 1963, '...] ... in Akhelo, they will constitute 24 evils for him.', 'P.Lond. 98', 'horoscope', 'Egypt', '95 CE', 'GH 95', '', '120', 2297, '97', '');
INSERT INTO public.quotations VALUES (254, 'ki si-man ša₂ GI₆ DU ⸢al-la LA₂ ša₂⸣ GI₆ DIRI ⸢LA₂ ša₂⸣ G[I₆ TA GI₆ DU NIM]', '', 260, 'If the time by which the night has progressed exceeds the length of night you subtract the length of night [from] (the time by which) the night has progressed.', 'BM 35399 (BMAPT No. 61A)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. 1', 354, '396-397', '');
INSERT INTO public.quotations VALUES (1312, 'Appellatur autem, sicut Abraham designat, Lunae locus.', '', 1167, '[The lot of Fortune] is also called—as Abraham points out—Place of the Moon.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.17.5', 1406, '1.239', '');
INSERT INTO public.quotations VALUES (967, 'Nec hoc est admiratione dignum, cum sciamus inter ipsos quanta sit ipsa deorum naturae dissensio quantisque disputationum argumentis uim totam divinitatis conantur evertere', '', 863, 'This is not surprising since we know how much difference of opinion there is among them about the nature of the gods, and with how many different theories they are trying to destroy the whole force of astrological divination.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.1.3', 38909671, '1.5', '');
INSERT INTO public.quotations VALUES (693, 'Στίλβων Ἀπόλλωνος', '', 620, 'The shining one (Mercury), (the star of) Apollo', 'IGLS I 36 (GH –61)', 'horoscope', '', '', '', '', '', 815, '', '');
INSERT INTO public.quotations VALUES (1010, 'Signa autem ipsa duodecim his nominibus nominantur: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpius, Sagittarius, Capricornus, Aquarius et Pisces.', '', 903, 'The twelve signs themselves are called by these names: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpio, Sagittarius, Capricorn, Aquarius, and Pisces.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.2', 1108, '1.41', '');
INSERT INTO public.quotations VALUES (109, 'Τῶν δὲ Χηλῶν τοῦ Σκορπίου οἱ μὲν ἐν ἄκραις αὐταῖς ὡσαύτως διατιθέασι τῷ τε τοῦ Διὸς καὶ τῷ τοῦ Ἑρμοῦ. οἱ δὲ ἐν μέσαις τῷ τε τοῦ Κρόνου καὶ ἠρέμα τῷ τοῦ Ἄρεως', '', 135, 'Of the Claws of the Scorpion, the (stars) in the very tips (sc. of the claws) dispose (their effects) similarly to the (star) of Jupiter and the (star) of Mercury, and the ones in the middles, to the (star) of Saturn and the one of Mars', 'Ptolemy, Tetrabiblos', 'astrology', '', '2nd cent. CE', '', '', '1.9.8', 183, '', '');
INSERT INTO public.quotations VALUES (1947, 'tunc enim proteguntur, quotienscumque oportuna radiatione praecesserint.', '', 1731, 'they will be protected whenever they precede with a favorable aspect', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.8.3', 2036, '1.51', '');
INSERT INTO public.quotations VALUES (41, 'κατέναντι δὲ τῆς κεφαλῆς τῆς Μεγάλης Ἄρκτου ὁ Ἡνίοχος ἔχει τοὺς ὤμους λοξὸς ὢν ὑπὲρ τοὺς πόδας τῶν Διδύμων, ἔχων τὸν δεξιὸν πόδα κοινὸν τῷ ἐν ἄκρῳ τῷ ἀριστερῷ κέρατι τοῦ Ταύρου', '', 91, '"Opposite the head of the Great Bear, the Charioteer, which stands at a slant, has its shoulders above the feet of the Twins (i.e. Gemini), sharing its right foot with the (star) at the tip of the left horn of the Bull (i.e. Taurus)"', 'Eudoxus apud Hipparchus 1.2.10', 'astronomy', '', '4th cent. BCE', '', '', '', 111, '', '');
INSERT INTO public.quotations VALUES (829, '', 'pꜣ-rꜥ kꜣ 4 (pꜣ)-nṯr-(twꜣ)', 746, 'Sun (in) Taurus 4°: (the terms) of Venus.', 'ANAshm.Mus.D.O. 633', 'horoscope', 'Athribis', '44 BCE', 'Winkler, A., 2022. The First zodiac Sign and the Daimon: The Advent of an Astrological Tradition and Seven Elaborate Horoscopes, Studien zur Altägyptischen Kultur 51:', '', '3', 936, '305', '');
INSERT INTO public.quotations VALUES (43, 'Ταῦρός ἐστι θηλυκόν, στερεόν, κείμενον ἐν τῇ ἐαρινῇ τροπῇ, ὀστῶδες, μελοκοπούμενον, ἀνατέλλον ἐκ τῶν ὀπισθίων, δῦνον δὲ ὀρθόν, οὗ τὸ πλεῖστον μέρος ἐν τῷ ἀφανεῖ κόσμῳ κεῖται', '', 91, 'Taurus is a feminine (sign), firm, lying in the vernal equinox (lit. springtime turning), bony, with mutilated limbs, rising back to front, but setting vertically, whose greater part lies in the invisible part of the cosmos', 'Vettius Valens 1.2.8', 'astrology', '', '', '2nd cent. CE', '', '', 112, '', '');
INSERT INTO public.quotations VALUES (675, 'καὶ γὰρ ὁ κλῆρος ἐν ἀποκλίσει εὑρέθη καὶ ὁ κύριος τοῦ τριγώνου τῆς Σελήνης Ἄρης', '', 605, 'For the lot (of Fortune) was found in a declining place, and the lord of the triangle of the Moon was Ares.', 'Vettius Valens', 'horoscope', '', '2nd century CE', '(GH L 75)', '', '4.8.22', 798, '', '');
INSERT INTO public.quotations VALUES (111, 'ὅταν δὲ αἱ Χηλαὶ ἀνατέλλωσι, συνανατέλλει μὲν αὐταῖς ὁ ζῳδιακὸς ἀπὸ Χηλῶν μοίρας ιϛʹ ἕως Σκορπίου μοίρας ϛʹ· μεσουρανεῖ δὲ ἀπὸ Καρκίνου ιηʹ ἕως Λέοντος ιαʹ', '', 135, 'When the Claws rise, there rises with them the zodiac from 16 degrees of Libra until 6 degrees of Scorpio, and culminates (lit. is in midheaven) from 18 of Cancer until 11 of Leo', 'Hipparchus', 'astronomy', '', '2nd cent. BCE', '', '', '3.3.4', 184, '', '');
INSERT INTO public.quotations VALUES (704, 'mul.MUŠ', '', 329, 'Snake-star.', 'VAT 7847+AO 6448', 'microzodiac text', 'Uruk', 'early 2nd cent BCE', 'Weidner, E., 1967. Gestirn-Darstellungen auf babylonischen Tontafeln. Wien: Hermann Böhlaus Nachf.', '', 'caption for the drawing of the snake-dragon', 444, '9', '');
