INSERT INTO public.quotations VALUES (1080, 'quicquid in decreto celatur, ex duodecatemoriis posse prodi significant.', '', 936, 'whatever is hidden in the chart can be revealed by the duodecatemoria', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.13.1', 1176, '1.55', '');
