INSERT INTO public.quotations VALUES (2149, 'Multos cometas non videmus, quia obscurantur radiis solis; quo deficiente quondam cometen apparuisse, quem sol vicinus obtexerat, Posidonius tradit.', '', 1903, 'We do not see many comets because they are obscured by the rays of the sun. Posidonius reports that once during an eclipse a comet appeared which the nearness of the sun had concealed.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.269.xml', '7.20.4', 2229, '268-269', '');
