INSERT INTO public.quotations VALUES (1400, 'Turn yero in omnibus proficiet, cum Saturnus primam stationem in Leone vel in Capricorno solverit.', '', 1249, 'Everything will go forward when Saturn moves from his first station in Leo or in Capricorn.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '5.3.10', 1492, '2.31', '');
