INSERT INTO public.quotations VALUES (607, '(15) kun-nu u [nu]-bal-kut ME ana ME kun-nu
(16) la nu-bal-kut 1-en ME LA₂ tur-ru la nu-bal-kut ME ana ME tur-ru u nu-bal-kut 1-en ME TAB', '', 543, '(15) (The month) is confirmed and (the day) is transgressed: the day (corresponds) to the (same) day. (The month) is confirmed (16), (the day) is not transgressed: you subtract one day. (The month) is returned and (the day) is not transgressed: the day (corresponds) to the (same) day. (The month) is returned and (the day) is transgressed: you add one day.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'rev. ii 15-16', 719, '', '');
INSERT INTO public.quotations VALUES (240, 'a-na 1 DANNA 8 u NIM u SIG', '', 249, 'For 1 bēru it (Jupiter) ascends and descends 8 fingers.', 'BM 37266 (BMAPT No. 47)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. 4', 335, '334', '');
INSERT INTO public.quotations VALUES (786, 'TA 13 ABSIN0 EN 27 zib NU TUK', '', 703, 'From 13 Virgo until 27 Pisces (you add) nothing.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. ii 25''', 895, '362-363', '');
INSERT INTO public.quotations VALUES (787, 'NU TUK u₃ 10 NINDA ⸢GAR⸣.[GAR-ma 10]', '', 703, 'You accumulate nothing and (0;0),10, [it is (0;0),10.]', 'BM 36680 (Ossendrijver 2017 Text C)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-150 BCE', 'M. Ossendrijver, 2017, New results on a Babylonian scheme for Jupiter''s motion along the zodiac, Journal of Near Eastern Studies 76, 231–247', '', 'obv. 7''', 895, '238', '');
INSERT INTO public.quotations VALUES (623, 'Ἥλιος, Ἑρμῆς Ταύρῳ, Σελήνη Ὑδροχόῳ, Κρόνος, Ἀφροδίτη Κριῷ, Ζεὺς Παρθένῳ, Ἄρης Ἰχθύσιν, ὡροσκόπος Λέοντι. τὸν μὲν οὖν Ἥλιον εὕρομεν τριγώνῳ Ἀφροδίτης καὶ Σελήνης, τὴν δὲ Ἀφροδίτην ἐν ἀποκλίματι', '', 559, 'Sun, Mercury in Taurus, Moon in Aquarius, Saturn, Venus in Aries, Jupiter in Virgo, Mars in Pisces, Ascendant in Leo. We found the Sun in the triangle of Venus and the Moon, and Venus in a falling place', 'Vettius Valens', 'horoscope', '', '', '(GH L 83)', '', '2.22.17–18', 738, '', '');
INSERT INTO public.quotations VALUES (450, '', '(n)-ṯꜣy pꜣ gn⟨ḥ⟩ḏ 6 šꜥ ỉḫ.t 26 r swsw 110 ỉw=f mšꜥ | tny ỉwṱ 20 r mꜣꜥ ḫꜥ 3 r swsw 1 ỉw=k ỉr 3 {100} sp 110 r 330', 395, 'From Cancer 6° to Libra 26° is 110 degrees, proceeding at the rate of 0;20, as a part of rising, 3 (of them) make 1 degree. You shall multiply 3 times 110, it makes 330.', 'ANAshm.Mus.D.O. 483', 'mathematical astronomy: procedure text', 'Egypt', '3rd cent. BCE to 3rd cent. CE', 'Ossendrijver, M. and A. Winkler, 2018. Chaldeans on the Nile: Two Egyptian Procedure Texts with Babylonian System A1 and A2 for Mercury, in Scaffolding of Our Thoughts: Essays on Assyriology and the History of Science in Honor of Fracesca Rochberg, eds. J. Crisostomo et al. (Leiden: Brill, 2018), 282-418', '', '5-6', 559, '386', '');
