INSERT INTO public.quotations VALUES (913, '...] | ε | [...] | [... ...] | Μεχ | [...', '', 759, '...] | 5 | [...] | [... ...] | Mech(eir) | [...', 'P. Oxy. LXI 4151', 'mathematical astronomy: epoch table', 'Oxyrhynchus', 'after 254 CE', 'Jones, A. (ed.), 1999. Astronomical papyri from Oxyrhynchus (P. Oxy. 4133-4300a), Volumes I and II. Philadelphia: American Philosophical Society.', 'https://archive.org/details/astronomicalpapy0001jone', '17', 953, 'I: 122-123 + II: 62-65', '');
INSERT INTO public.quotations VALUES (2080, 'Caelum autem dividitur in circulos quinque, qui per mundi cardines eunt: est septemtrionalis, est solstitialis, est aequinoctialis, est brumalis, est contrarius septemtrionali.', '', 1845, 'The sky is divided into five circles which pass through the cardinal points of the universe. In these five circles are the northern circle, the summer solstitial circle, the equinoctial circle, the wintry circle, and the one that is opposite to the northern.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.111.xml', '5.17.2', 2166, '110-111', '');
INSERT INTO public.quotations VALUES (308, '36 taš AB₂ ana AB₂', '', 298, '(0);36 is the difference month to month.', 'BCM A.1845-1982.2+ab (BMAPT No. 97)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. ii 11''', 406, '', '');
