INSERT INTO public.quotations VALUES (1958, 'et adhuc intra Solis orbem destituta luminibus Luna claudatur, nec renatis luminibus deserto Solis orbe lucescat', '', 1743, 'and the Moon is still enclosed in the orb of the Sun, devoid of light, and does not [yet] shine with reborn light after leaving the Sun''s orb', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.25.4', 2047, '2.128', '');
INSERT INTO public.quotations VALUES (314, '', 'ḥsb.t 5.t n Ꜣntwnyns | ỉbd-3 (n) šmw sw ꜥrḳy n pꜣ Wynyn pꜣ ṯꜣy-wnw.t 3.t n grḥ | pꜣ-rꜥ sbk ḥr-tšy mꜣe', 305, 'Regnal year 5 of Antoninus (Pius) | month 3 of Shemu, day 30 according to the Greeks, 3rd hour of the night | The sun, Mercury, and Mars in Leo.', 'Griffith MSS 3.59', 'horoscope', 'Thebes (?)', '2nd century CE', 'A. Winkler, A Starry Summer Night in AD 142: A Theban Horoscope (Griffith MSS 3.59) in Context, in K. Donker van Heel, F.A.J. Hoogelijk, and C.J. Martin (eds), Hieratic, Demotic and Greek Studies and Text Editions: Of Making Many Books There Is No End. Festschrift for Sven P. Vleeming (Leiden:Brill), 298-308.', '', '1-3', 413, '301', '');
INSERT INTO public.quotations VALUES (1082, 'Nunc ad expositionem cardinum locorumque omnium transferatur intentio', '', 971, 'Now we must give our attention to the cardinal points and places', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.14.1', 1178, '1.57', '');
INSERT INTO public.quotations VALUES (2581, 'Δελέφατ· ὁ τῆς Ἀφροδίτης ἀστήρ, ὑπὸ Χαλδαίων', '', 2253, 'Delephat: the star of Aphrodite, amongst the Chaldeans.', 'Hesychius', 'lexicon', 'Alexandria', '4th-6th century CE', 'Hesychius Alexandrinus. 2018. Hesychii Alexandrini Lexicon, Vol. 1 (A - Delta), ed. I.C. Cunningham. Berlin, Boston: Walter de Gruyter.', 'https://doi.org/10.1515/9783110547405', 'α 590', 2649, '558', '');
INSERT INTO public.quotations VALUES (234, '28 MUL₂.BABBAR ina SAG MAS.MAS IGI 10 UŠ NA-su', '', 239, '(Day) 28, Jupiter appears in the beginning of Gemini, its NA (= time from sunset to setting of Jupiter) is 10 UŠ.', 'BM 34794+ (ADRT I No. -324B)', 'astronomical diary', 'Babylon', '325 BCE', 'Sachs, A. J., Hunger, H. 1988. Astronomical Diaries and Related Texts from Babylonia, Vol. I: Diaries from 652 B.C. to 262 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 11''', 326, '194-195', '');
INSERT INTO public.quotations VALUES (1633, 'ζητήσαντες καθὼς προεκτεθείμεθα εὕρομεν τὴν ἐναντίωσιν Ἄρεως καὶ Ἀφροδίτης χρηματίζουσαν οὕτως κατὰ τὸ ζʹ κλίμα καὶ τὴν τετράγωνον πλευρὰν Κρόνου', '', 1445, 'By searching in the manner in which we have set out, we found the opposition of Mars and Venus thus active according to the 7th clime, and the square side (i.e., quadrangle) of Saturn', 'Vettius Valens', 'astrological treatise', '', '2nd century CE', '(GH L 124)', '', '7.6.30', 1717, '', '');
