INSERT INTO public.quotations VALUES (889, 'γένεσις Φιλόης ιγ (ἔτους) Ἀντωνίνο[υ] Καίσαρος τοῦ κυρίου Φαμε(νὼθ) ιεʹ εἰς ιϛʹ ὥρας δʹ νυκτός', '', 780, 'Nativity of Philoe, in the 13th year of the lord Antoninus Caesar, (month of) Phamenoth, the 15th into the 16th, 4th hour of the night', 'P.Oslo II 6', '', '', '150 CE', '(GH 150)', '', '1–3', 977, '', '');
INSERT INTO public.quotations VALUES (1050, 'Nam si deficientem Lunam deficiens Venus exceperit aut Mars in nocturna genitura, tunc prospera omnia deficiens Luna felicitatesque decernit', '', 940, 'If Venus and Mars aspect the waning moon in a nocturnal chart, the waning Moon indicates all kinds of prosperity and good fortune', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.7.3', 1144, '1.49', '');
INSERT INTO public.quotations VALUES (1960, 'Antiscium itaque eius in Ariete constitutum <et> Solis antiscium, quod in Libram fuerat missum, per diametrum sibi facta contraria amplissimi ordinis decreto in exilium mitti fecerunt ob hoc, quod Saturni et Solis antiscia in cosmicis constituta sideribus de diametro se viderunt.', '', 1745, 'they do it, because the antiscia of Saturn and the Sun are located in cosmic stars in opposition [to each other?]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.29.13', 2049, '1.82', '"Meaning uncertain, but perhaps Firmicus means that that [sic] actual planets are opposed in the heavens in addition to their shadow-degrees." (Dykes, Benjamin. Firmicus Maternus: Mathêsis. )');
INSERT INTO public.quotations VALUES (792, 'ἀνέρχεται βορρᾶν ἐπὶ βαθμοῦ τρίτου ἐπέχουσα τοῦ βαθμοῦ τὸν ιʹ κδʹ', '', 707, '(The Moon) goes up to the north in the third step, occupying the 1/10th (+) 1/24th (0;8,30) of the step.', 'CCAG IV', 'horoscope', '', '', '(GH L 497)', '', 'p. 106', 899, '', '');
INSERT INTO public.quotations VALUES (1024, 'Veneris domus sunt Taurus et Libra, ex quibus Libra masculinum signum est, femininum vero Taurus.', '', 917, 'The signs/houses of Venus are Taurus and Libra, of which Libra is masculine and Taurus is feminine.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.2.5', 1122, '1.43', '');
