INSERT INTO public.quotations VALUES (784, '(6) KI sin NIM KI sin u KI šamaš₂ ša₂ al-la 10 lu-maš DIRI GAM0 16 DU KI BAR.NUN ša₂ l[u-maš] (7) ša₂-šu₂ ki TAB TAB ki LA₂ LA₂ ana muḫ-ḫi a-ḫa-miš₂ GAR.GAR-ma ½-šu₂-nu GIŠ-ma ana BAR.NUN ⸢GAR⸣-[an]', '', 701, '(6) The position of the Moon and the position of the Sun: (the amount) by which they exceed 10 of the zodiacal sign you multiply by (0;0),16. You add it with the corresponding ṣiliptu-coefficient for the zod[iac] if it is increasing, subtract if it is decreasing. You accumulate (them) together, compute half of it and put it down as the ṣiliptu-coefficient.', 'BM 35399 (BMAPT No. 61A)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. 6-7', 893, '397-398', '');
