INSERT INTO public.quotations VALUES (1296, 'Si vero per diem <in> principalibus geniturae locis sic fuerit inventa, is paternam substantiam dissipat,', '', 1154, 'But if it is found in important places of the chart in that way, it squanders the inheritance', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.4.5', 1389, '1.203', '');
