INSERT INTO public.quotations VALUES (987, 'Ecce cognoscitur [...] quando frigus eius fomento alieni ardoris incalescat', '', 883, 'This is how we can recognize [...] how his cold is warmed by another''s heat', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.4.6', 1080, '1.12', '');
INSERT INTO public.quotations VALUES (318, '[... GI₆ 19] DIR AN ZA ULU₃ GIN ina ZALAG₂ sin ar₂ MAŠ.MAŠ ar₂ 1 KUŠ₃ sin 5 KUŠ₃ ana ULU₃ SIG', '', 302, '[... Night of (day) 19], clouds were in the sky, the south wind blew; last part of the night, Sin was 1 cubit behind the rear twin (star), Sin being 5 cubits low to the south.', 'BM 35015+ (ADRT II No. -164B)', 'astronomical diary', 'Babylon', '165 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 7''', 410, '494-495', '');
