INSERT INTO public.quotations VALUES (1299, 'sed illis intra cutem malignus humor semper inlapidatos articulos obligat, praesertim si per vacuum currens, quam Graeci cenodromon dicunt', '', 1157, 'they will suffer from malignant humors under the skin which will attack their joints, especially if the moon is running empty (void of course), which the Greeks call κενοδρόμος', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.8.1', 1392, '1.207', '');
INSERT INTO public.quotations VALUES (922, 'ἡ Σελήνη φερομένη ἐπὶ τὸν οἰκοδέκτορα αὐτῆς καὶ ἐπιβλέπουσα τὸν κύριον τοῦ κλήρου τῆς Τύχης Ἄρεα καὶ τὴν τούτου ὁμοίωσιν· καὶ ὁ Ἥλιος ἀπὸ τριγώνου βλέπων τὸν ὡροσκόπον καὶ κατὰ ἑξάγωνον τὴν Τύχην καὶ κατὰ συμπαρουσίαν τὴν κυρίαν τοῦ ὡροσκόπου', '', 809, 'The Moon, moving towards its house-receiver and looking toward (being in aspect with) the master of the lot of Fortune, Mars, and the likeness of this one, and the Sun, looking at (being in aspect with) the Ascendant from trine (aspect) and at the (lot of) Fortune according to sextile (aspect) and in co-presence (conjunction) with the mistress of the Ascendant', 'CCAG VI', 'horoscope', '', '', '(GH L 478)', '', 'p. 65', 1008, '', '');
INSERT INTO public.quotations VALUES (2321, 'et quinque in partes aquilonis distat ab orbe.', '', 2034, 'and [the tropic of Cancer] is five degrees distant from the northern circle', 'Manilius, Astronomica', 'astrological treatise', '', '', 'Manilius. Astronomica. Edited and translated by G. P. Goold. Loeb Classical Library 469. Cambridge, MA: Harvard University Press, 1977.', 'https://www.loebclassics.com/view/manilius-astronomica/1977/pb_LCL469.41.xml', '1.574', 2391, '50-51', '');
