INSERT INTO public.quotations VALUES (2053, 'Sed ut ad id de quo agitur revertar, venti quattuor sunt, in ortum, occasum, meridiem septemtrionemque divisi', '', 1822, 'But to return to the subject which is being discussed: there are four winds, divided amongst the East, the West, the South, and the North.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.105.xml', '5.16.1', 2139, '104-105', '');
INSERT INTO public.quotations VALUES (2165, 'Dic tu mihi prius quare luna dissimillimum soli lumen accipiat, cum accipiat a sole; quare modo rubeat, modo palleat; quare lividus illi et ater color sit, cum conspectu solis excluditur.', '', 1917, 'Tell me first why the moon receives light totally unlike the sun although she receives it from the sun. Why does the moon sometimes grow red, sometimes pale? Why is her colour blue or black when she is cut off from the sun’s view?', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.283.xml', '7.27.1', 2245, '282-283', '');
