INSERT INTO public.quotations VALUES (2055, 'Ab oriente hiberno eurus exit, quem nostri vocavere vulturnum—T. Livius hoc illum nomine appellat in illa pugna [...]', '', 1824, 'Eurus comes from the winter sunrise [S.E.], a wind which our people call Vulturnus. T. Livy calls it by this name in connection with that famous battle [...]', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.107.xml', '5.16.4', 2141, '106-107', '');
