INSERT INTO public.quotations VALUES (1267, 'iti.KIN 13 KI ŠU2 20 | AN.GE6 30 al šal ḪAB-rat | DIRI GAR-an ad2 E3-a | ina ZIB.ME', '', 1127, 'Month VI, the 13th, with the setting of the sun | a lunar eclipse, exceeding 1/3 of the disk, | occured; (the moon) was darkly when it rose | in Pisces.', 'BM 38104 (BH 27)', 'horoscope', 'Babylon', '1st century BCE (date of birth in horoscope: 243 S.E. I.20 = -68 Apr.16)', 'Rochberg, F. 1998. Babylonian Horoscopes. Transactions of the American Philosophical Society, 88.', '', '5-8', 1358, '137', '');
INSERT INTO public.quotations VALUES (652, 'dil-bat GU4.UD i MUL2 ŠID.MEŠ IGI.MEŠ MUL2.BABBAR u AN ša2 ina bi-ib-lu ina AN.GE6-šu2 IGI.MEŠ', '', 576, 'Venus, Mercury and the Normal Stars were visible; Jupiter and Mars, which were in their period of invisibility, were visible in its eclipse.', 'BM 45745 (ADRT III No. -136B)', 'astronomical diary', 'Babylon', '137 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', 'http://oracc.museum.upenn.edu/adsd/adart3/sig?☣%40adsd%2Fadart3%25akk%3Abi-ib-lu%3Dbiblu%5B(action%20of)%20bringing%2F%2F(period%20of)%20invisibility%5DN´N%24biblu', 'rev. 14''', 765, '184-185', '');
INSERT INTO public.quotations VALUES (2588, 'Ἀδάρ· μὴν παρὰ Χαλδαίοις', '', 2258, 'Adar: a month according to the Chaldeans.', 'Hesychius', 'lexicon', 'Alexandria', '4th-6th century CE', 'Hesychius Alexandrinus. 2018. Hesychii Alexandrini Lexicon, Vol. 1 (A - Delta), ed. I.C. Cunningham. Berlin, Boston: Walter de Gruyter.', 'https://doi.org/10.1515/9783110547405', 'α 972', 2654, '46', 'In the manuscript, only the reading Ἀδαεὶ is attested.');
INSERT INTO public.quotations VALUES (1234, 'Mercurius cum Iove partiliter in horoscopo constitutus in diurna genitura faciet claros et gloriosos', '', 1107, 'Mercury conjunct Jupiter on the ascendant in a day-chart make [the native] famous and glorious', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.10.1', 1327, '1.171', '');
INSERT INTO public.quotations VALUES (1969, '', 'ỉw=w ḏd n=k [fy 15 2/3 r 100] | ỉw=k šꜥṱ 6 ẖ[n 100 sp 94] | iw=k ḏd ḫr-š[m 6 r 94 r1]5 2/3 | my ỉn=f 1/3 r 47', 1756, 'If it is said to you [carry 15 2/3 to 100,] | you subtract 6 from [100, 94 remains]. | You shall say [6] goes to 94 with the result 1]5 2/3 | Cause it to be brought to 1/3 result 47.', 'P.Cairo J.E. 89127', 'mathematical text', 'Tuna el-Gebel', '3rd cent. BCE', 'R.A. Parker, 1972, Demotic Mathematical Texts, Brown Egyptological Studies 7, Providence RI/London.', '', '1-4', 2059, '37', '');
INSERT INTO public.quotations VALUES (2058, 'Non enim eodem semper loco sol oritur aut occidit, sed alius est ortus occasusque aequinoctialis—bis autem aequinoctium est—alius solstitialis, alius hibernus.', '', 960, 'For the sun does not always rise or set in the same place. One rising and setting is the equinoctial [i.e., rising due E., setting due W.]—moreover, the equinox occurs twice a year—another is the summer-solstitial [i.e., rising in the N.E., setting in the N.W.], and still another the winter-solstitial [i.e. rising in the S.E., setting in the S.W.].', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.107.xml?viewAnnotation=296988', '5.16.4', 2144, '106-107', '');
