INSERT INTO public.quotations VALUES (997, 'tu vero, Gradive Mars rutilo semper horrore metuende, qui in tertiis caeli regionibus contineris', '', 892, 'and you, Mars Gradivus, always with your fearful red color, whose home is the third region of the sky', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.10.14', 1090, '1.38', '');
INSERT INTO public.quotations VALUES (1656, 'In Arietis parte XXX., quae pars totum signum supra terram semper ostendit, exoritur Capra, quam fabulosi poetae alimenta volunt Iovi inmulsisse nutricia.', '', 1466, 'At the 30th degree of Aries which always holds the whole sign above the earth rises the She-goat. The poets said she was the fabled nurse of Jupiter.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.6.7', 1741, '2.297', '');
INSERT INTO public.quotations VALUES (2069, 'A septemtrionali latere summus ere summus est aquilo, medius septemtrio, imus θρασκίας; huic deest apud nos vocabulum.', '', 1823, 'In the northern section the highest is Aquilo [N.N.E.], the middle Septemtrio [N.], the lowest is Thrascias [N.N.W.], for which there is lacking a word in our language.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.109.xml', '5.16.6', 2155, '108-109', '');
INSERT INTO public.quotations VALUES (2176, 'Ex his quidam circuli in transversa currunt et alios interventu suo scindunt.', '', 1925, 'Out of these some circles run transversely and split the other circles by intersecting them.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.111.xml', '5.17.3', 2256, '110-111', '');
INSERT INTO public.quotations VALUES (1795, 'in extremis vero pedibus XXVIII. et XXIX. sunt,', '', 1598, 'in the outer(?) legs [of the Crab] are [the degrees] 28 and 29', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.4.4', 1885, '2.289', '');
INSERT INTO public.quotations VALUES (1981, 'satius erit de re ipsa quaerere quam mirari quid ita Aristoteles globum ignis appellaverit capram. Talis enim fuit forma eius qui bellum adversus Persen Paulo gerente lunari magnitudine apparuit.', '', 1466, 'It will be more appropriate to investigate the phenomenon itself rather than to wonder why Aristotle has called a ball of fire a Goat. Actually, that was the shape of a fire which did appear, about the size of the moon, when Paulus was waging war against Perseus.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.15.xml', '1.1.2', 2070, '14-15', '');
