INSERT INTO public.quotations VALUES (2178, 'Non religionem incutit mentibus, et quidem publice, sive deficere sol visus est, sive luna (cuius obscuratio frequentior) aut parte sui aut tota delituit?', '', 1927, 'Does it not inspire religion in men’s minds, and even on a nationwide scale, if the sun is seen in eclipse, or the moon (whose eclipses are more frequent) is obscured in part or entirely?', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.143.xml', '6.3.3', 2258, '142-143', '');
INSERT INTO public.quotations VALUES (1324, 'Si enim septeni et noveni anni, qui ebdomadici a Graecis et enneadici appellantur, gravia pericula hominibus semper indicunt, quid faciet LXIII. annus, qui utriusque numeri multiplicatam [...] perficit [...]?', '', 1176, 'For if every seventh and ninth year, which are called ἐβδομαδικός by the Greeks and ἐννεαδικός, always indicate grave danger for humans, what will the 63rd year do, which is both numbers multiplied?', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.20.3', 1415, '1.258-259', '');
