INSERT INTO public.quotations VALUES (1396, 'Sed tunc decernit uxorem, cum parodicus Saturnus (id est temporalis) aut Lunam de quadrato viderit, aut Sol ipsum de Leone vel Sagittario trigonica radiatione respexerit.', '', 1245, 'But then he indicates the wife when Saturn having completed a full return (i. e. temporal) aspects either the Moon in a square or the Sun in a trine from Leo or Sagittarius.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '5.3.3', 1488, '2.28', '');
