INSERT INTO public.quotations VALUES (1459, 'i-nu-šu2 30 šap-lat SI GIR2.TAB', '', 1295, 'At that time, the moon was below the Horn of the Scorpion.', 'AB 251 (BH 2)', 'horoscope', 'Babylon?', '3rd century BCE (date of birth in horoscope: [Darius II Year 14].I.14 = -409 April 29)', 'Rochberg, F. 1998. Babylonian Horoscopes. Transactions of the American Philosophical Society, 88.', '', 'obv. 3', 1545, '56', '');
INSERT INTO public.quotations VALUES (479, '28 AN.MI šamaš₂ ki PAP NU IGI', '', 439, '(Day) 28, eclipse of Šamaš, when I watched I did not see it.', 'BM 34609 (ADRT III No. -144)', 'astronomical diary', 'Babylon', '145 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 10''', 592, '', '');
