INSERT INTO public.quotations VALUES (2076, 'Aequinoctialis occidens favonium mittit, quem zephyrum esse dicent tibi etiam qui graece nesciunt loqui.', '', 1841, 'The equinoctial sunset [W.] sends Favonius, which even those who do not know how to speak Greek will tell you is Zephyr.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.109.xml', '5.16.5', 2162, '108-109', '');
INSERT INTO public.quotations VALUES (2181, 'sed sic quomodo aequinoctium in calorem frigusque flectentis anni, quomodo illa quae Chaldaei canunt, quid stella nascentibus triste laetumve constituat.', '', 1930, 'but in the way that the equinox is a sign of the year turning to hot or to cold, or as the things the Chaldaeans predict, the sorrow or joy that is established at people’s birth by a star.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.287.xml', '7.28.1', 2261, '286-287', '');
