INSERT INTO public.quotations VALUES (1485, '29.31.50.19.11.4.56 ŠID AB₂ KUR ina ZI MURUB₄ AB₂ a-na AB₂', '', 1309, '29;31,50,19,11,4,56, the number (of days) of the month for (the Moon) reaching (the Sun) with the mean displacement, month by month.', 'BM 36705 (BMAPT No. 82)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'obv. 15''', 1575, '470-471', '');
