INSERT INTO public.quotations VALUES (2077, 'Hanc lineam, quae inter aperta et occulta est (id est hunc circulum), Graeci ὁρίζοντα vocant, nostri finitorem esse dixerunt, alii finientem.', '', 1842, 'The line (that is, this circle) which exists between the visible and the invisible sector is what the Greeks call the horizon line; some of us call it the limiter line, others call it the limiting line.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.111.xml', '5.17.3', 2163, '110-111', '');
