INSERT INTO public.quotations VALUES (2183, 'Sed mendacium est ire eos tardius, nam intra sextum mensem dimidiam partem caeli transcurrit hic proximus, prior intra pauciores menses recepit se.', '', 1932, 'But it is not true that they move slowly, for this last comet ran across half the sky in only six months. The previous comet withdrew in fewer months.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.289.xml', '7.29.2', 2263, '288-289', '');
INSERT INTO public.quotations VALUES (1475, '[... A] ⸢ša₂ md.EN-A-URU₃⸣-šu₂ A md.A₂.GAL₂-d.TU.TU qat₃-šu₂ im-šuḫ-ma ana TIL GAR-un', '', 1306, '[... son] of Bel-apla-uṣuršu, descendant of Ile”i-Marduk. His hand "measured" it, and he finished it.', 'BM 34789+ (BMAPT No. 36)', 'mathematical astronomy: synodic table', 'Babylon', '2nd century BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer', '', 'rev. 1 (colophon)', 1565, '296', '');
INSERT INTO public.quotations VALUES (1521, 'In ferinis enim et in agrestibus constituti signis, ad necem praeparatos ferarum adferunt morsus.', '', 1349, 'Located in animal and rural signs they bring death at the teeth of wild beasts.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.29.11', 1613, '2.135', '');
INSERT INTO public.quotations VALUES (97, '(6) KI sin u KI šamaš₂ ša₂ al-la 10 lu-maš DIRI GAM0 16 DU KI BAR.NUN ša₂ l[u-maš] (7)  ša₂-šu₂ ki TAB TAB ki LA₂ LA₂', '', 121, 'The position of Sin and the position of Šamaš: that which exceeds 10 (UŠ) of the zodiac sign you multiply times 0;16, add with the ṣiliptu-coefficient for the zodiac sign if it is increasing, subtract if it is decreasing.', 'BM 35399 (BMAPT No. 61A)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-50 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. 6-7', 165, '', '');
