INSERT INTO public.quotations VALUES (1674, 'In Scorpii parte XII. oritur Centaurus.', '', 1484, 'At 12° Scorpio rises the Centaur.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.13.2', 1759, '2.308', '');
INSERT INTO public.quotations VALUES (2185, 'Deinde hic proximus a septentrione motus sui initium fecit et per occidentem in meridiana pervenit erigensque cursum suum oblituit, alter ille Claudianus, a septentrione primum visus, non desiit in rectum assidue celsior ferri, donec excessit.', '', 1934, 'In the second place, this recent comet started its motion in the north and passing through the west it arrived in the southern region and its orbit passed out of sight as it was rising. The other comet, the one in the time of Claudius, was first seen in the north, and was carried continuously straight up and did not stop until it disappeared.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.289.xml', '7.29.3', 2265, '288-289', '');
INSERT INTO public.quotations VALUES (1990, 'Varia et multa terrarum orbis expirat, quaedam umida, quaedam sicca, quaedam calentia, quaedam concipiendis ignibus idonea.', '', 891, 'the terrestrial globe exhales many different particles, some wet, some dry, some hot, some suitable for starting fires.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.19.xml', '1.1.7', 1401, '18-19', '');
INSERT INTO public.quotations VALUES (1506, 'Martis enim inconsulta temeritas et ardoris inprobitas et inflammati caloris ardor inpatiens coniuncto sibi Saturni frigore temperatur,', '', 1332, 'The association with Saturn reduces the unthinking rashness and impatient ardor of Mars and blunts it with Saturn''s coldness', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.22.5', 1598, '2.115', '');
