INSERT INTO public.quotations VALUES (1904, 'GE₆ ⸢14⸣ [x]+1 GE₆ DIR muš SAG GE₆ sin ina IGI SI₄ 1/2 KUŠ₃ ana SI-šu₂ SIG LAL₂', '', 1696, 'Night of (day) 14, GE₆ (= sunset to moonrise) was [x]+1 (UŠ), measured (despite) clouds; beginning of the night, Sin was 1/2 cubit in front of Lisi (= α Sco), it was balanced to its lower horn.', 'BM 32889+ (ADRT II No. -246)', 'astronomical diary', 'Babylon', '247 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 5', 1988, '', '');
INSERT INTO public.quotations VALUES (1519, 'Quodsi sic malivolis sicut diximus constitutis horoscopo Venus aut luppiter testimonium conmodaverint, expositus colligetur et alienae misericordiae praesidio nutrietur.', '', 1347, 'If as we said Venus and Jupiter aspect the malefic planets on the ascendant favorably, the exposed infant will be picked up and nourished by a stranger', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.29.6', 1611, '2.134', '');
INSERT INTO public.quotations VALUES (1529, 'Si [...] horoscopante Marte Venus cum Mercurio in occasu fuerit constituta [...]', '', 1358, 'If Venus and Mars are located on the descendant while Mars is on the ascendant [...]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '6.31.17', 1621, '2.153', '');
INSERT INTO public.quotations VALUES (1991, 'sed acrior sit Caniculae rubor, Martis remissior, Iovis nullus in lucem puram nitore perducto.', '', 1773, 'The red of the Dog Star is brighter, that of Mars weaker, while Jupiter has no red, with its gleam extended into pure light.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.19.xml', '1.1.7', 2079, '18-19', '');
INSERT INTO public.quotations VALUES (2186, 'Sideris proprium est ducere orbem.', '', 1935, 'It is characteristic of a planet to follow a curve.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.273.xml', '7.23.1', 2266, '272-273', '');
INSERT INTO public.quotations VALUES (2264, 'nec norunt obitus unoque in vertice mutant
INSERT INTO public.quotations VALUES (1132, 'Scire etiam debemus quod sit dextrum trigonum, quod sinistrum. Dextrum trigonum est quod ab eo signo, a quo incipimus, retro est, sinistrum vero, quod abante; utputa Arietis dextrum trigonum est Sagittarius, sinistrum vero Leo', '', 1019, 'We must know too which is a right and which is a left trine. The right trine is that which is back of the sign from which we begin, the left trine is the one ahead. So, for instance, the right trine of Aries is Sagittarius, the left is Leo', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.22.4', 1227, '1.69', '');
