INSERT INTO public.quotations VALUES (1599, '3 1/3 MA.NA MIN 2/3 DANNA i-na KI.MIN 36 lim DANNA ina KI.MIN | TA mul.GAŠAN-TIN EN qu₂-ma-ri ša₂ mul.UD.⸢KA⸣.DUḪ.A', '', 1419, '3 1/3 minas ditto (weight), 2/3 bēru on ditto (the ground), 36 thousand bēru in ditto (heaven), | from the Lady of Life to the Shoulder of the Demon-with-the-gaping-mouth.', 'AO 6478 (TCL VI 21 = TU 21)', 'star list', 'Uruk', 'ca. 200 BCE', 'Thureau-Dangin 1913, ''Distances entres étoiles fixes d''après une tablette de l''époque Séleucides'', RA 10, 215-225', '', 'obv. 17''-18''', 1688, '218', '');
INSERT INTO public.quotations VALUES (214, '', '(n)-ṯꜣy pꜣ gn⟨ḥ⟩ḏ 6 šꜥ ỉḫ.t 26 r swsw 110 ỉw=f mšꜥ | tny ỉwṱ 20 r mꜣꜥ ḫꜥ 3 r swsw 1 ỉw=k ỉr 3 {100} sp 110 r 330', 223, 'From Cancer 6° to Libra 26° is 110 degrees, proceeding at the rate of 0;20, as a part of rising, 3 (of them) make 1 degree. You shall multiply 3 times 110, it makes 330.', 'ANAShm.Mus.D.O. 483', 'mathematical astronomy: procedure text', 'Egypt', '3rd cent. BCE to 3rd cent. CE', 'Ossendrijver, M. and A. Winkler, 2018. Chaldeans on the Nile: Two Egyptian Procedure Texts with Babylonian System A1 and A2 for Mercury, in Scaffolding of Our Thoughts: Essays on Assyriology and the History of Science in Honor of Fracesca Rochberg, eds. J. Crisostomo et al. (Leiden: Brill, 2018), 282-418', '', '', 309, '386', '');
INSERT INTO public.quotations VALUES (928, '', 'r ḥꜣ|.ṱ⸗y šlf | n-tr.ṱ|⸗k mny sp-sn.w ỉw=y | ỉr r tw=y ḥp|ḥ(p) ỉw=y pl|ḏ ḏmꜥ ỉw=y  qty| n-ỉm=y m-sꜣ nꜣy m|šy wḥm ỉw=y | ḥpḥ(p) ỉrm (ḥr-pꜣ)-šwṱ | ḥtr.w', 814, 'I was ashamed because of you daily. I was disturbed. I unrolled (the) papyrus roll. I searched the repeated movement (of planets) and I was disturbed with Jupiter in Gemini.', 'OMM 75', 'school text', 'Narmouthis', '2nd cent. CE', 'A. Menchetti, Un aperçu des textes astrologiques de Medinet Madi, in G. Widmer and D. Devauchelle, eds., Actes du IXe Congrès International des Études Démotiques: Paris, 31 août - 3 septembre 2005; Paris, 2009, 223-241.', '', '1-12', 1013, '232-233', '');
INSERT INTO public.quotations VALUES (1600, '', 'mh̭y tꜣ.wy rn⸗f ⌈ḏd⌉ tꜣ ỉh̭e tꜣ.wy ḏr⸗w tꜣy', 1420, 'The balance of the Two Lands  is his name because it is the horizon of every land', 'P.Berol. 13603', 'theological treatise', 'Egypt', '1st cent. BCE', 'W. Erichsen - S. Schott, Fragmente memphitischer Theologie in demotischer Schrift (Pap. demot. Berlin 13603), Abhandlungen der Akademie der Wissenschaften und der Literatur Mainz, Geistes- und sozialwiss. Kl., Jg. 1954, Nr. 7, Wiesbaden 1954', '', 'vi 21', 1689, '', '');
INSERT INTO public.quotations VALUES (1995, 'Atqui, quo solet, quaeque [stellae] invenitur loco et magnitudo sua singulis constat;', '', 972, 'Still, each star is found in its usual place and its size remains constant.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.19.xml', '1.1.10', 2083, '18-21', '');
INSERT INTO public.quotations VALUES (1679, 'In X. parte Capricorni oritur Lyra.', '', 1489, 'At 10° Capricorn rises the Lyre.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', '', '8.15.3', 1764, '3.310', '');
INSERT INTO public.quotations VALUES (665, '', '[nysn | pꜣ wr]š ỉbd-4 ꜣḫ[.t ꜣyr pꜣ] wrš tpy [pr.t] šywn pꜣ wrš ỉbd-2 pr.t [r ỉbd 3 ỉs ybr | tmws] pꜣ wrš [ỉbd-3 pr.t] ꜣb pꜣ wrš ỉbd-4 pr.t ꜣll [pꜣ wrš tpy šmw r ỉbd 3 ỉs | ꜥy]mr t[ršy [pꜣ wrš] ỉbd-2 šmw mrḥšwn pꜣ wrš ỉbd-3 š[mw kslw pꜣ wrš | ỉbd-4 š]mw r ỉbd 3 [ỉs kmy] tbṱ [pꜣ wrš] tpy ꜣḫ.t šb[ṱ] pꜣ wrš [ỉbd-2 ꜣḫ.t ꜣtr pꜣ | wrš ỉbd-3 ꜣ[ḫ.t r ỉbd 3 ỉs pꜣ] tš ỉ[š}w[r] tmt ỉbd 12', 596, '[Nisan: | lunar] month 4 of Akhet; [Iyyar:] lunar month 1 of [Peret;] Siwan: lunar month 2 of Peret [make the 3 months belonging to the Hebrews | Tammuz:] lunar month [3 of Peret;] Abu: lunar month 4 of Peret; Elul: [lunar month 1 of Shemu make the 3 months belonging to | the Am]orite; Ti[shri: lunar month] 2 of Shemu; Marheshwan: lunar month 3 of Sh[emu; Kislev: lunar month 4 of Sh]emu make the 3 months belonging to Egypt; Tebeth: [lunar month] 1 of Akhet; Sheb[at:] lunar month [2 of Akhet; Adar: | lunar month 3 of Ak]het make the 3 months belonging to the] land of Assur: total 12 months.', 'P.Omina', 'celestial divination', 'Soknopaiou Nesos', '2nd century CE; text from 7th cent. BCE', 'R. A. Parker, 1959, A Vienna Demotic Papyrus on Eclipse- and Lunar Omina , Providence .', '', 'iv 10-15', 783, '20-24', '');
INSERT INTO public.quotations VALUES (1692, 'Lygnus quoque ad huius signi pertinet partem.', '', 1503, 'Lygnus is also part of this sign [Anguis].', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.17.8', 1777, '2.314', '');
