INSERT INTO public.quotations VALUES (529, '', 'ḫr-ỉr=k=f n-ṯꜣy-n pꜣ hrw | n mḥ-4 n pꜣ wrš šꜥ pꜣ hrw n mḥ-15 nty-ỉw 15.nt pꜣy ỉw ỉꜥḥ mḥ wḏꜣ.t', 447, 'You always do it from the | 4th day of the lunar month until day 15, which is the Full Moon day, when the Moon fills the Udjat-eye.', 'GEMF 16', 'magical text', 'Thebes', '2nd cent. CE; text from 7th cent. BCE', 'Faraone, Ch. and Torallas Tovar, S. 2022, Greek and Egyptian Magical Formularies: Text and Translation, vol. I (Berkeley CA)', '', '294-295', 646, '174', '');
