INSERT INTO public.quotations VALUES (773, '', 'ỉn ỉw=f ḥr wnmy n pꜣ twtw ⌈ṯꜣy⌉ [pꜣ sw]sw ⌈mḥ⌉-1 šꜥ mḥ-12 twtw n ḥwṱ ⌈n⌉ wmnmy', 690, 'If it is on the right of the zodiac sign> from the 1st until the 12th degree, that means male zodiac sign on the right;', 'P.Carlsberg XI 10', 'literary text', 'Tebtunis', '2nd Cent. CE', 'Quack, J. F. and K. Ryholt, 2019, Petese Interpreting Astrology by Imhotep for King Nechepsos. Copenhagen, 2019, 161-183.', '', '12', 883, '165', '');
INSERT INTO public.quotations VALUES (926, '', 'r ḥꜣ|.ṱ⸗y šlf | n-tr.ṱ|⸗k mny sp-sn.w ỉw=y | ỉr r tw=y ḥp|ḥ(p) ỉw=y pl|ḏ ḏmꜥ ỉw=y  qty| n-ỉm=y m-sꜣ nꜣy m|šy wḥm ỉw=y | ḥpḥ(p) ỉrm (ḥr-pꜣ)-šwṱ | ḥtr.w', 811, 'I was ashamed because of you daily. I was disturbed. I unrolled (the) papyrus roll. I searched the repeated movement (of planets) and I was disturbed with Jupiter in Gemini.', 'OMM 75', 'school text', 'Narmouthis', '2nd cent. CE', 'A. Menchetti, Un aperçu des textes astrologiques de Medinet Madi, in G. Widmer and D. Devauchelle, eds., Actes du IXe Congrès International des Études Démotiques: Paris, 31 août - 3 septembre 2005; Paris, 2009, 223-241.', '', '2-12', 1011, '232-233', '');
INSERT INTO public.quotations VALUES (739, '', '[nꜣ rmṯ.w nty] ỉw=w r ms.ṱ=w r ỉꜥḥ ḥr tꜣy=f ẖlꜣ ḫm', 662, '[The people who] are born when the Moon is in its small ''lunar phase''', 'P.Carlsberg XI 10', 'literary text', 'Tebtunis', '2nd Cent. CE', 'Quack, J. F. and K. Ryholt, 2019, Petese Interpreting Astrology by Imhotep for King Nechepsos. Copenhagen, 2019, 161-183.', '', '14', 855, '165', '');
