INSERT INTO public.quotations VALUES (1601, '24 DIR AN ZA SI GIN ina KIN.SIG ŠU₂', '', 404, 'The 24th, clouds were in the sky, the north wind blew; in the afternoon, overcast.', 'BM 34636 (ADRT III No. -141C)', 'astronomical diary', 'Babylon', '142 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 6''', 545, '118-119', '');
INSERT INTO public.quotations VALUES (1602, '⸢19⸣ IM.SI ŠAR₂ KALAG GE₆ 20 IM ŠAR', '', 404, 'The 19th, strong gusty north wind. Night of the 20th, gusty wind.', 'Rm 728 (ADRT II No. -230C)', 'astronomical diary', 'Babylon', '231 BCE', 'Sachs, A. J., Hunger, H. 1989. Astronomical Diaries and Related Texts from Babylonia, Vol. II: Diaries from 261 B.C. to 165 B.C. Vienna: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 7''', 545, '118-119', '');
