INSERT INTO public.quotations VALUES (163, '', 'ḥm-nṯr spd.t ỉmy-r(ꜣ) mšꜥ n ỉꜥḥ wꜥb n p(ꜣ) 5 sw ꜥnḫ.w nth rḫ pꜣ wš n ỉr ꜥb n rꜥ ỉꜥḥ', 169, 'Prophet of Sothis, overseer of the moon''s movement, priest of the 5 living stars, who knows the time of obscuration of the sun and the moon.', 'Graf.Dak. 30', 'inscription', 'Dakka', '3rd cent. CE', 'Griffith, F.Ll.,   Catalogue of the Demotic Graffiti of the Dodecaschoenus (Oxford, 1935 - 1937)', '', '4', 246, '26-31', '');
INSERT INTO public.quotations VALUES (2462, 'parte genus variant et vires linea mutat.', '', 914, 'The signs vary their nature according to their quarter of the sky, and a line which joins them likewise suffers change of its strength.', 'Manilius, Astronomica', 'astrological treatise', '', '', 'Manilius. Astronomica. Edited and translated by G. P. Goold. Loeb Classical Library 469. Cambridge, MA: Harvard University Press, 1977.', 'https://www.loebclassics.com/view/manilius-astronomica/1977/pb_LCL469.133.xml', '2.645', 2526, '132-133', '');
