INSERT INTO public.quotations VALUES (1832, 'DIŠ 1 MUL MU2-uḫ : U4 1-KAM ina ŠA3 <<ina ŠA3>> d.UDU.IDIM.MEŠ IGI-ma : ša2-niš MUL2 <ana> ŠA3-šu2 | KU4-ma', '', 1550, '¶ "If one star rises" (means): on the 1st day among «among» the planets it appears; alternatively (it means): a star enters inside it.', 'AO 6464 (TCL VI 17 = TU 17)', 'commentary', 'Uruk', 'late 4th century BCE', 'Hunger, H. 1995. Ein Kommentar zu Mond-Omina. In: Vom Alten Orient zum Alten Testament, Festschrift für Wolfram Freiherrn von Soden zum 85. Geburtstag am 19. Juni 1993, eds.W. Dietrich and O. Loretz. Butzon & Kevelaer: 105-118.', 'https://ccp.yale.edu/P363690', 'rev. 18-19', 1830, '', '');
INSERT INTO public.quotations VALUES (1326, 'Hac ex causa ab Aegyptiis androclas dictus est, quod omnem viri substantiam frangat ac debilitet.', '', 1150, 'Therefore this year is called ἀνδροκλάς by the Egyptians, because it breaks and debilitates all substance of life.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.20.3', 1417, '1.258', '');
INSERT INTO public.quotations VALUES (2190, '(6) AN.GE₆ sin al šal ḪAB-rat (7) DIRI GAR-an ad₂ E₃-a', '', 1939, '(6) An eclipse of Sin occurred, it exceeded a third of the disk, (7) it rose eclipsed.', 'BM 38104 (BH 27)', 'horoscope', 'Babylon', '1st century BCE (date of birth in horoscope: 243 S.E. I.20 = -68 Apr.16)', 'Rochberg, F. 1998. Babylonian Horoscopes. Transactions of the American Philosophical Society, 88.', '', 'rev. 6-7', 2270, '137-139', '');
INSERT INTO public.quotations VALUES (2012, 'Quomodo nunc me hoc loco geram? Quid vocem? Imagines solis?2 Historici soles vocant et binos ternosque apparuisse memoriae tradunt.', '', 1791, 'Now, how am I to express myself in this section?2 What should I call these things? Images of the sun? Historians call them “suns” and record that they have appeared two and three at a time.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.67.xml', '1.11.2', 2100, '66-67', '');
