INSERT INTO public.quotations VALUES (2409, '', 'rḫ ḫd(.t) ḫnt n ỉtn šsr bỉꜣ.w=f ḥnꜥ dỉ.t-r(ꜣ)=sn dỉ(.w) ḫp(r) ḏd=f ḫpr.n=sn ỉy nw=sn', 169, 'The one who knows the sun''s northward and southward migration, the one who  announces its eclipses together with its statements, which generates what he has said is what has came to pass, coming (in) its (correct) time', 'JE 38545', 'autobiographical inscription', 'Egypt', '330-30 BCE', 'Altmann-Wendling, Victoria and Marco Stockhusen 2020. Die Statue eines ägyptische Priesters und Astronomen (JE 38545). In Färber, Roland and Rita Gautschy (eds), Zeit in den Kulturen des Altertums: antike Chronologie im Spiegel der Quellen, 103-115. Wien; Köln; Weimar: Böhlau', '', '106', 246, '', '');
INSERT INTO public.quotations VALUES (1081, 'quicquid in decreto celatur, ex duodecatemoriis posse prodi significant.', '', 970, 'whatever is hidden in the chart can be revealed by the duodecatemoria', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.13.1', 1177, '1.55', '');
INSERT INTO public.quotations VALUES (1278, 'sed et plenas partes ac vacuas earumque decreta monstrabo', '', 970, 'but I will also show the full and empty degrees and their indications', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.0.4', 1371, '1.196', '');
