INSERT INTO public.quotations VALUES (1308, 'Si vero per noctem minuta luminibus Saturno retrograde vel stationem facienti se Luna coniunxerit, magnarum infelicitatum decernit <incommoda>;', '', 1163, 'If however in a night chart the waning Moon aspects Saturn while in retrograde or stationary, it indicates major misfortune', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.16.10', 1402, '1.236', '');
INSERT INTO public.quotations VALUES (1866, 'tres sequentes in ventre,', '', 1663, 'the three following [degrees] are in the belly [of Capricorn]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965186/html', '8.4.10', 1953, '2.292', '');
INSERT INTO public.quotations VALUES (1871, 'DU₃-šu₂ ša₂ NIM u SIG ša₂ sin AB₂ ana AB₂ 12 DAGAL ma-lak ᵈsin 2.24 MURUB₄-ti qaq-qar ki-ṣa-ri', '', 1668, 'Procedure for the Moon’s ‘height and depth’, month by month. The width of the path of the Moon is 12. The central part, the region of the node, is 2,24.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'obv. i 20''', 1958, '', '');
