INSERT INTO public.quotations VALUES (2191, '[...] ⸢x⸣ sin ki E₃-a gab-bi-šu₂ ŠU₂-im ana ZALAG₂-ru ki TAB-u₂ [...]', '', 1939, '[...] ... when the moon rose, it was completely covered; when it began to clear, [...]', 'BM 45654 (ADRT III No. -170A)', 'astronomical diary', 'Babylon', '171 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'rev. 12''', 2270, '', '');
