INSERT INTO public.quotations VALUES (1238, 'Babylonii enim [in] duodecatemoriis summam decretorum tribuunt, Ptolomaeus vero antisciis, nos utrisque.', '', 936, 'The Babylonians attribute all the forecast to the duodecatemorion; but Ptolemy to the antiscia, and we to both.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.13.14', 1176, '1.191', '');
INSERT INTO public.quotations VALUES (2014, 'Graeci parhelia appellant, quia in propinquo fere a sole visuntur aut quia accedunt ad aliquam similitudinem solis.', '', 1792, 'The Greeks call them “parhelia,” either because they are commonly seen near the sun, that is, alongside it, or because they are near to some resemblance to the sun.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.69.xml', '1.11.2', 2102, '68-69', '');
INSERT INTO public.quotations VALUES (2105, 'Duo certe, qui apud Chaldaeos studuisse se dicunt, Epigenes et Apollonius Myndius, peritissimus inspiciendorum natalium, inter se dissident.', '', 1865, 'Certainly, two authorities, who say they studied among the Chaldaeans, disagree with each other, Epigenes and Apollonius of Myndus, the latter highly skilled in casting horoscopes.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.233.xml', '7.4.1', 2189, '232-233', '');
INSERT INTO public.quotations VALUES (975, 'cur [...] mortem doloresque contemnimus, si gloriae laudem et potentissimae dignitatis insignia nec appetentibus nobis Sol Iovis bona coniunctionis societate largitur?', '', 871, 'why [...] do we despise pain and death [...], if praise and rewards come from the Sun in favorable association with Jupiter?', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.2.6', 1068, '1.7', '');
