INSERT INTO public.quotations VALUES (2193, 'GE₆ 11 sin TA DIR ki E-a SIG DELE-ša₂-IGI-ABSIN 1 KUŠ₃ sin 1/2 KUŠ₃ ana NIM DIB', '', 1939, 'Night of (day) 11, when the moon emerged from a cloud, it was 1 cubit below the single (star) in front of the Furrow, passing 1/2 cubit to the east.', 'BM 45708 (ADRT III No. -125A)', 'astronomical diary', 'Babylon', '126 BCE', 'Sachs, A. J., Hunger, H. 1996. Astronomical Diaries and Related Texts from Babylonia, Vol. III. Diaries from 164 B.C. to 61 B.C. Vienna, Austria: Verlag der Österreichischen Akademie der Wissenschaften.', '', 'obv. 4', 2271, '258-259', '');
