INSERT INTO public.quotations VALUES (2022, 'Omnis de universo quaestio in caelestia, sublima, terrena dividitur.', '', 1799, 'Any study of the universe is divided into uranology [the study of the sky], meteorology, and geography.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.99.xml', '2.1.1', 2110, '98-99', '');
INSERT INTO public.quotations VALUES (2113, 'Talem effigiem ignis longi fuisse Callisthenes tradit, antequam Burin et Helicen mare absconderet. Aristoteles ait non trabem illam sed cometen fuisse; ceterum ob nimium ardorem non apparuisse sparsum ignem sed, procedente tempore, cum iam minus flagraret, redditam suetam cometis faciem.', '', 1332, 'Callisthenes reports that a similar likeness of an extended fire appeared just before the sea covered Buris and Helice. Aristotle says that this was not a Beam but a comet. Moreover, he says that because of its excessive brightness the fire did not appear scattered but as time went on and it blazed less it recovered the usual appearance of a comet.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.237.xml', '7.5.4', 2196, '236-237', '');
