INSERT INTO public.quotations VALUES (2114, 'Duo, inquit Epigenes, cometarum genera sunt. Alii ardorem undique effundunt nec locum mutant; alii in unam partem ignem vagum in modum comae porrigunt et stellas praetermeant, quales duo aetate nostra visi sunt.', '', 1872, 'There are, Epigenes says, two types of comets. Some pour out fire in all directions and do not change position. Others extend a vague fire like hair in one direction and travel among the stars, like the two seen in our own time.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.239.xml', '7.6.1', 2197, '238-239', '');
