INSERT INTO public.quotations VALUES (1128, 'Appellatur autem hic locus a nobis Bonus Daemon vel Bonus Genius', '', 1002, 'This house is called by us Bonus Daemon or Bonus Genius', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.19.12', 1211, '1.64', '');
INSERT INTO public.quotations VALUES (2023, 'Separat, quia medius intervenit; iungit, quia utrique per hunc inter se consensus est; supra se dat quicquid accepit a terris, rursus vim siderum in terrena transfundit.', '', 1801, '[The atmosphere] separates because it intervenes midway; it joins because through it there is a communication between the two. It transmits to the upper region whatever it receives from the earth. On the other hand, it transfuses to earthly objects the influences of the stars.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.105.xml', '2.4.1', 2111, '104-105', '');
