INSERT INTO public.quotations VALUES (885, '(11'') ana tar-ṣa 1 : ŠU.SI ḪAB-rat ḫe-pit₂-tu₂ 1.⸢38⸣.2⸢4⸣ (12'') NIM u SIG GAR-an ša₂ al 1 ḪAB-rat ḫe-pit₂-tu₂ LA₂ TA 1 LA₂ ša₂ TA[G₄ A.RA₂ 6 DU] (13'') KI 1.38.24 NIM u SIG TAB-ma ana SIG NIM GAR-an', '', 773, '(11'') Opposite 1 finger, the broken disk, you put 1,3[8];24 (12’) as the height and depth. That which is less than 1, the broken disk, you deduct from 1. What rem[ains you multiply by 6,] (13'') add to 1,38;24, the ‘height and depth’, and you put it down as the depth, ascending.', 'BM 32167+ (BMAPT No. 53)', 'mathematical astronomy: procedure text', 'Babylon', 'ca. 350-250 BCE', 'Ossendrijver, M. 2012. Babylonian Mathematical Astronomy: Procedure Texts. New York: Springer.', '', 'rev. ii 11''-13''', 970, '', 'The intended meaning of "broken" is unclear.');
INSERT INTO public.quotations VALUES (2115, 'Cometas autem immixtos stellis videmus per superiora labentes.', '', 1873, 'But indeed, we see comets mixed in with the stars, gliding through the upper regions.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.243.xml', '7.8.4', 2198, '242-243', '');
