INSERT INTO public.quotations VALUES (1109, 'Dea Deus Bona fortuna ac Bonus daemon, quae a Graecis hactenus nominantur: thea, theos, agathe tyche, agathos daemon', '', 997, 'Goddess, God, Good fortune, Bad fortune, which are called by the Greeks as follows: thea, theos, agathe tyche, agathos daemon', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.16.1', 1206, '1.59', '');
INSERT INTO public.quotations VALUES (2557, 'aliena capessunt
INSERT INTO public.quotations VALUES (1116, 'Residua quattuor loca pigra et deiecta esse dicuntur ob hoc quod nulla cum horoscopo societate iunguntur.', '', 1004, 'The remaining four houses are all feeble and debilitated because of the fact that they are not aspected to the ascendant.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1213, '1.60', '');
INSERT INTO public.quotations VALUES (1115, 'Residua quattuor loca pigra et deiecta esse dicuntur ob hoc quod nulla cum horoscopo societate iunguntur.', '', 1003, 'The remaining four houses are all feeble and debilitated because of the fact that they are not aspected to the ascendant.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1212, '1.60', '');
INSERT INTO public.quotations VALUES (1117, 'Est autem ex his residuis quattuor locis primus, qui in secundo ab horoscopo signo constituitur: qui locus Inferna Porta dicitur vel Anafora', '', 1005, 'The first of thses remaining four places, however, which is located in the second place from the ascendant, is called the Gate of Hell, or the Anafora (rising up from the Underworld)', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1214, '1.60', '');
INSERT INTO public.quotations VALUES (1119, 'in diametro huius signi locus qui fuerit, id est in VIII. ab horoscopo signo, Epicatafora appellatur', '', 1006, 'The place which is diametrically opposite to this place, that is, the eighth from the ascendant, is called the Epicatafora', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1216, '1.60', '');
INSERT INTO public.quotations VALUES (1120, 'et Mala Fortuna in VI. ab horoscopo signo constituitur.', '', 1007, 'and Bad fortune is located in the sixth sign from the ascendant', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1217, '1.60', '');
INSERT INTO public.quotations VALUES (1121, 'sed Malam fortunam Graeci cacen tychen appellant', '', 1008, 'but Bad fortune the Greeks call κακὴν τύχην', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1218, '1.60', '');
INSERT INTO public.quotations VALUES (1122, 'Malus vero Daemon in XII. ab horoscopo signo ponitur', '', 1010, 'but the Malus Daemon is located in the twelfth sign from the ascendant', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1219, '1.60', '');
INSERT INTO public.quotations VALUES (1175, 'Post Saturnum luppiter accepit temporum potestatem', '', 1059, 'After Saturn, Jupiter received the rulership of time', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.1.12', 1269, '1.94', '');
INSERT INTO public.quotations VALUES (1134, 'Cum datorem vitae diligenter inspexeris, id est dominum geniturae, [...]', '', 1021, 'If you look closely at the giver of life, i. e. the ruler of the chart [...]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.25.2', 1229, '1.73', '');
INSERT INTO public.quotations VALUES (1123, 'Malum daemonem [...] cacodaemonem Graeci vocant', '', 1011, 'The bad daemon the Greeks call cacodaemon', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.17.1', 1220, '1.60', '');
INSERT INTO public.quotations VALUES (1130, 'Quemcumque enim locum benivolae stellae respexerint, [...] omnia [...] feliciter proueniunt', '', 1017, 'For when the benefic planets are in aspect [...] all the things [...] turn out well', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.20.4', 1225, '1.66', '');
INSERT INTO public.quotations VALUES (1054, 'si vero plena lumine uel crescens Luna Veneri vel Marti se aliqua radiatione coniunxerit, maximas calamitates et maxima facit infortunia concitari.', '', 944, 'But if a full or waxing Moon comes into aspect with Venus or Mars, it brings about the greatest catastrophes and stirs up enormous ill fortune', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.7.3', 1148, '1.49', '');
INSERT INTO public.quotations VALUES (1129, 'His locus a Graecis cacos daemon appellatur', '', 1011, 'This house is called by the Greeks κακὸς δαίμων', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.19.13', 1220, '1.65', '');
INSERT INTO public.quotations VALUES (1135, 'Cum datorem vitae diligenter inspexeris, id est dominum geniturae, [...]', '', 1022, 'If you look closely at the giver of life, i. e. the ruler of the chart [...]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.25.2', 1230, '1.73', '');
INSERT INTO public.quotations VALUES (1125, 'respicere enim horoscopum de exagono videtur.', '', 1013, 'it seems to be aspected in sextile to the ascendant', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.19.4', 1222, '1.62', '');
INSERT INTO public.quotations VALUES (1124, 'respicere enim horoscopum de exagono videtur.', '', 1012, 'it seems to be aspected in sextile to the ascendant', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.19.4', 1221, '1.62', '');
INSERT INTO public.quotations VALUES (1131, 'Scire etiam debemus quod sit dextrum trigonum, quod sinistrum. Dextrum trigonum est quod ab eo signo, a quo incipimus, retro est, sinistrum vero, quod abante; utputa Arietis dextrum trigonum est Sagittarius, sinistrum vero Leo', '', 1018, 'We must know too which is a right and which is a left trine. The right trine is that which is back of the sign from which we begin, the left trine is the one ahead. So, for instance, the right trine of Aries is Sagittarius, the left is Leo', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.22.4', 1226, '1.69', '');
INSERT INTO public.quotations VALUES (1139, 'Gemini in Cancrum antiscium mittunt et Cancer in Geminos', '', 1026, 'Gemini send antiscia toward Cancer and Cancer toward Gemini', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.29.3', 1234, '1.78', '');
INSERT INTO public.quotations VALUES (1137, 'Cum itaque Sol dominus temporum fuerit, tenet dominatum annos decem menses novem; simili modo et Luna, cum domina temporis fuerit, tenebit annos decem menses novem.', '', 1024, 'Thus when the Sun is the Ruler of Time he holds dominion for ten years and nine months. In a similar way the Moon, when she becomes Mistress of Time, will hold it for ten years and nine months.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.26.2', 1232, '1.75', '');
INSERT INTO public.quotations VALUES (1038, 'Sol in Arietis parte XIX. exaltatur, in Libra vero parte XIX. deicitur', '', 929, 'The Sun is exalted at 19° Aries but is in its fall at 19° Libra', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.3.5', 1133, '1.44', '');
INSERT INTO public.quotations VALUES (1144, 'doctrinam etiam et tantam litterarum scientiam immutatis domibus Saturnus Mercuriusque decreverunt', '', 1030, 'Such learning and knowledge are also decreed by Mercury and Saturn when their signs are interchanged', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.29.19', 1238, '1.84', '');
INSERT INTO public.quotations VALUES (1136, 'Si itaque dominus vitae, vel dator vitae, id est dominus geniturae, in domo sua vel in altitudine sua uel finibus suis fuerit [...]', '', 1023, 'If the Ruler of Life or the Giver of Life, that is the ruler of the nativity, is in his own house or in his own exaltation or in his own terms [...]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.25.10', 1231, '1.74', '');
INSERT INTO public.quotations VALUES (1033, 'Scire etiam altitudinem et humiliationem debemus', '', 923, 'We must also know about exaltation and fall', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.3.1', 1128, '1.43', '');
INSERT INTO public.quotations VALUES (1140, 'Si quae se itaque stellae in ordinatione geniturae non viderint, quaerendum est an sibi per antiscium sociata radiatione iungantur.', '', 1027, 'Thus if, in computing the chart, some of the planets are not in aspect, it must be asked whether they are connected to each other through the relationship of the antiscia.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.29.8', 1235, '1.80', '');
INSERT INTO public.quotations VALUES (1142, 'in Occasu posita, violenta Martis radiatione pulsetur.', '', 1029, 'located in the descendant, it is badly aspected with Mars', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.29.17', 1237, '1.84', '');
INSERT INTO public.quotations VALUES (1310, 'partiliter vero locus Fortunae ista ratione colligitur, quam tu sequi in omni disputatione debebis.', '', 1165, 'the exact location of the Lot of Fortune is found in this manner which you must follow in every computation [of a nativity]', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.17.2', 1404, '1.238', '');
INSERT INTO public.quotations VALUES (1179, '[...], praesertim si in malignae stellae vel signo vel loco vel finibus fuerit inventus.', '', 1063, '[...] especially if he is in the house or terms of a malefic planet.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.2.21', 1273, '1.103', '');
INSERT INTO public.quotations VALUES (1018, 'Signa autem ipsa duodecim his nominibus nominantur: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpius, Sagittarius, Capricornus, Aquarius et Pisces.', '', 911, 'The twelve signs themselves are called by these names: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpio, Sagittarius, Capricorn, Aquarius, and Pisces.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.2', 1116, '1.41', '');
INSERT INTO public.quotations VALUES (1067, 'Nunc de XII signorum natura, formis, qualitate, substantiis, anaforis explicare debemus', '', 957, 'We must now explain about the nature of the twelve signs, their appearance, essential qualities, their principles and risings', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.10.1', 1161, '1.52', '');
INSERT INTO public.quotations VALUES (1284, 'Est itaque Luna aut synodica aut plena aut dichotomos aut menoides aut amficyrtos et per has mutata formas cursum menstrui luminis complet.', '', 1141, 'Therefore the moon is either new or full or half or crescent or gibbous and changed through these shapes she completes the course of monthly light.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.1.10', 1377, '1.199', '');
INSERT INTO public.quotations VALUES (1173, 'Lunae tarnen per quadratum testimonium perhibet;', '', 1057, 'she is in square aspect to the Moon.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.1.6', 1267, '1.93', '');
INSERT INTO public.quotations VALUES (1311, 'Quae omnia tunc explicabimus, cum ad interpretationem venerimus sfaerae barbaricae;', '', 1166, 'All this we will explain when we come to the interpretation of the sfaera barbarica;', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.17.2', 1405, '1.238', '');
INSERT INTO public.quotations VALUES (1178, 'Si vero Lunam de quadrato vel de diametro viderit aut si Luna in eadem parte fuerit qua ipse constituta, sterilitatem filiorum faciet;', '', 1062, 'If Saturn is in conjunction with the Moon or in opposition or square aspect to her, this will make the native sterile.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.2.9', 1272, '1.99', '');
INSERT INTO public.quotations VALUES (1285, 'Est itaque Luna aut synodica aut plena aut dichotomos aut menoides aut amficyrtos et per has mutata formas cursum menstrui luminis complet.', '', 1142, 'Therefore the moon is either new or full or half or crescent or gibbous and changed through these shapes she completes the course of monthly light.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.1.10', 1378, '1.199', '');
INSERT INTO public.quotations VALUES (1170, 'I. THEMA MUNDI.
INSERT INTO public.quotations VALUES (1187, 'In constantibus vero signis et quae modicae sunt vocis posita, in nocturna scilicet genitura, faciet multiplici studio veneratores amicos imperatorum', '', 1072, 'Venus in fixed signs or those with small voice in a nocturnal chart will make devoted friends of emperors, who make and invent things that please royalty;', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.6.3', 1280, '1.143', '');
INSERT INTO public.quotations VALUES (1188, 'Si enim hunc locum tenuerit in tropicis signis constituta vel squamosis vel [aut] erraticis, et cum ea Mars fuerit aut se illi per diametrum aut de quadrato forti radiatione coniunxerit, facit inpuros libidinosos infames.', '', 1073, 'If she is in tropical, scaly, or mutable signs, and Mars is with her or in opposition or square aspect, the natives are oversexed, impure, and of ill repute.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.6.14', 1281, '1.147', '');
INSERT INTO public.quotations VALUES (1184, 'iu aquosis constitutus naufragio submergit;', '', 1069, 'in watery signs he perishes in a boat;', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.4.20', 1277, '1.120', '');
INSERT INTO public.quotations VALUES (1186, 'In igneis vero signis constitutus exuri faciet.', '', 1071, 'In fiery signs he is burned up.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.4.23', 1279, '1.121', '');
INSERT INTO public.quotations VALUES (1185, 'In terrestribus vero signis in desertis locis faciet occidi, multiplici tarnen prius periculorum continuatione turbatos.', '', 1070, 'But in earth signs they die in desert places after being involved in many various dangers.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '3.4.23', 1278, '1.121', '');
INSERT INTO public.quotations VALUES (963, '[...] ut his respondeamus, qui totam vim matheseos multiplici orationum genere labefactere conantur [...]', '', 859, '[...] to answer those writers who are trying to destroy the whole theory of astrology by many different kinds of arguments', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '1.1.1', 468, '1.4', '');
INSERT INTO public.quotations VALUES (1314, 'Sin vero in signis cardinum non partiliter, sed platice fuerint inventi, mediae felicitatis incrementa decernunt.', '', 1169, 'If they are found not exactly but approximately in the angular places, they indicate a moderate increment in fortune.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.17.9', 1408, '1.241', '');
INSERT INTO public.quotations VALUES (1709, 'Inter oculos Cancri quicumque habuerint horoscopum, orbi vel unoculi erunt.', '', 1520, 'Whoever has the ascendant between the eyes of Cancer will be bereft and/or one-eyed.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '8.22.1', 1799, '1.326', '');
INSERT INTO public.quotations VALUES (1287, 'Est itaque Luna aut synodica aut plena aut dichotomos aut menoides aut amficyrtos et per has mutata formas cursum menstrui luminis complet.', '', 1144, 'Therefore the moon is either new or full or half or crescent or gibbous and changed through these shapes she completes the course of monthly light.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.1.10', 1380, '1.199', '');
INSERT INTO public.quotations VALUES (1315, 'Hie locus vocatur et animae substantia; ex hoc loco actus omnisque augmenta substantiae quaerimus [invenimus],', '', 1170, 'This lot is also called ''substance of the soul''; from this lot we ask/find the occupation and all increments in wealth', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.18.2', 1409, '1.242', '');
INSERT INTO public.quotations VALUES (1288, 'tertio rursus die diligenter debemus inspicere, et quibus se coniungat et <a> quibus defluat et recedat et quatenus se post primam coniunctionem secundis coniunctionibus societ', '', 1145, 'again on the third day we must observe diligently also to which [planets] [the Moon] forms aspect and from which it separates and recedes and how it forms second aspects after the first aspect', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.1.10', 1381, '1.199', '');
INSERT INTO public.quotations VALUES (1331, 'Si vero Mars sic Lunam positam minaci radiatione respexerit et utrique, idest Soli et Lunae, partili radiatione fuerit contrarius, corpus gravi vitio debilitat et indicit frequenter oculis caecitates.', '', 1239, 'If however Mars is aspected to the Moon in such a bad way and one of the two, i. e. Sun and Moon, are exactly opposite, he debilitates the body with a defect and often indicates blindness for the eyes', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.24.2', 1483, '1.274', '');
INSERT INTO public.quotations VALUES (1272, 'In hoc itaque libro id principe loco explicabitur, quod in posterioribus tertii libri partibus promisimus, idest Lunae omnes species ac formae, coniunctiones etiam, defluctiones et quicquid ad eius numinis pertinet potestatem;', '', 1131, 'In this book, in the first place, will be explained, what we promised in the last parts of book 3: the shapes and appearances of the moon, also the conjunctions, defluxions and whatever pertains to her power', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.0.4', 1365, '1.196', '');
INSERT INTO public.quotations VALUES (1336, 'Aries. In hoc signo, idest in Ariete, quae sint partes plenae, quae vacuae, speciali significatione monstrabimus. Itaque in Ariete plenae partes XII, vacuae partes XVIII.
INSERT INTO public.quotations VALUES (1338, 'Aries. In hoc signo, idest in Ariete, quae sint partes plenae, quae vacuae, speciali significatione monstrabimus. Itaque in Ariete plenae partes XII, vacuae partes XVIII.
INSERT INTO public.quotations VALUES (1020, 'Signa autem ipsa duodecim his nominibus nominantur: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpius, Sagittarius, Capricornus, Aquarius et Pisces.', '', 913, 'The twelve signs themselves are called by these names: Aries, Taurus, Gemini, Cancer, Leo, Virgo, Libra, Scorpio, Sagittarius, Capricorn, Aquarius, and Pisces.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '2.1.2', 1118, '1.41', '');
INSERT INTO public.quotations VALUES (1340, 'Aries. In hoc signo, idest in Ariete, quae sint partes plenae, quae vacuae, speciali significatione monstrabimus. Itaque in Ariete plenae partes XII, vacuae partes XVIII.
INSERT INTO public.quotations VALUES (1360, 'In Leonis signo quae sint partes plenae, quae vacuae, hoc demonstramus exemplo. Sunt autem plenae partes XXI, vacuae VIIII. 
INSERT INTO public.quotations VALUES (1333, 'Nunc illud tibi intimare debemus, qua ratione partiliter defluat Luna, qua etiam ratione iungatur, quas Graeci synafas aporroicas vocant.', '', 1241, 'Now we need to show you why exactly the Moon separates and for which reason it is joined [to other planets]—which the Greeks call συναφή and ἀπορροική.', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.25.1', 1485, '1.277', '');
INSERT INTO public.quotations VALUES (1343, 'In Tauri signo quae sint partes plenae, quae vacuae, hoc demonstratur exemplo. Sunt autem in Tauro plenae partes XX, vacuae partes X.
INSERT INTO public.quotations VALUES (1344, 'In Tauri signo quae sint partes plenae, quae vacuae, hoc demonstratur exemplo. Sunt autem in Tauro plenae partes XX, vacuae partes X.
INSERT INTO public.quotations VALUES (1346, 'In Tauri signo quae sint partes plenae, quae vacuae, hoc demonstratur exemplo. Sunt autem in Tauro plenae partes XX, vacuae partes X.
INSERT INTO public.quotations VALUES (1325, 'Si enim septeni et noveni anni, qui ebdomadici a Graecis et enneadici appellantur, gravia pericula hominibus semper indicunt, quid faciet LXIII. annus, qui utriusque numeri multiplicatam [...] perficit [...]?', '', 1135, 'For if every seventh and ninth year, which are called ἐβδομαδικός by the Greeks and ἐννεαδικός, always indicate grave danger for humans, what will the 63rd year do, which is both numbers multiplied?', 'Firmicus Maternus, Mathesis', 'astrological compendium', 'Sicily', 'ca. 334 CE', 'Firmicus Maternus. 1897. Ivlii Firmici Materni Matheseos Libri VIII, ed. W. Kroll, F. Skutsch. Stuttgart: B. G. Teubner.', 'https://www.degruyter.com/document/doi/10.1515/9783110965193/html', '4.20.3', 1416, '1.258-259', '');
INSERT INTO public.quotations VALUES (1345, 'In Tauri signo quae sint partes plenae, quae vacuae, hoc demonstratur exemplo. Sunt autem in Tauro plenae partes XX, vacuae partes X.
