INSERT INTO public.quotations VALUES (2027, '“Quid ergo? Non aliquando etiam apparentibus stellis et nocte tranquilla fulgurat?”', '', 1805, '“But well! Does it not sometimes flash lightning, even when the stars are visible and the night is clear?”', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume I: Books 1-3. Translated by Thomas H. Corcoran. Loeb Classical Library 450. Cambridge, MA: Harvard University Press, 1971.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL450.143.xml', '2.26.8', 2115, '142-143', '');
INSERT INTO public.quotations VALUES (2118, 'Atqui quia lunaris illa orbita ceterorumque supra lunam meantium motus irrevocabilis est nec haesitat usquam nec resistit nec dat ullam nobis suspicionem obiectae sibi morae', '', 1878, 'And yet the lunar orbit, and the movements of the other bodies travelling above the moon, is invariable. It nowhere hesitates or stops, nor does it give us any indication of an obstacle delaying it.', 'Seneca, Natural Questions', 'philosophy', '', '62/63 CE', 'Seneca. Natural Questions, Volume II: Books 4-7. Translated by Thomas H. Corcoran. Loeb Classical Library 457. Cambridge, MA: Harvard University Press, 1972.', 'https://www.loebclassics.com/view/seneca_younger-natural_questions/1971/pb_LCL457.247.xml', '7.10.2', 2201, '246-249', '');
